import { test, expect } from '@playwright/test';

test.describe('Accessibility Tests', () => {
  test.beforeEach(async ({ page }) => {
    // Login first
    await page.goto('/login');
    await page.locator('input[name="username"]').fill('admin');
    await page.locator('input[name="password"]').fill('AdminPassword123!');
    await page.locator('button[type="submit"]').click();
    await expect(page).toHaveURL(/.*\/dashboard/);
  });

  test('should have proper heading hierarchy', async ({ page }) => {
    // Check for proper heading structure
    const h1 = page.locator('h1');
    const h2 = page.locator('h2');
    const h3 = page.locator('h3');

    // Should have at least one h1
    await expect(h1).toHaveCount({ min: 1 });

    // Headings should be in logical order
    if (await h1.count() > 0) {
      await expect(h1.first()).toBeVisible();
    }
  });

  test('should have proper ARIA labels and roles', async ({ page }) => {
    // Check for navigation landmarks
    const nav = page.locator('nav, [role="navigation"]');
    if (await nav.count() > 0) {
      await expect(nav).toBeVisible();
    }

    // Check for main content area
    const main = page.locator('main, [role="main"]');
    if (await main.count() > 0) {
      await expect(main).toBeVisible();
    }

    // Check for buttons with proper labels
    const buttons = page.locator('button');
    for (let i = 0; i < Math.min(await buttons.count(), 5); i++) {
      const button = buttons.nth(i);
      const hasText = await button.textContent();
      const hasAriaLabel = await button.getAttribute('aria-label');
      const hasAriaLabelledBy = await button.getAttribute('aria-labelledby');

      // Button should have accessible name
      expect(hasText || hasAriaLabel || hasAriaLabelledBy).toBeTruthy();
    }
  });

  test('should support keyboard navigation', async ({ page }) => {
    // Test tab navigation
    await page.keyboard.press('Tab');

    // Should focus on first focusable element
    const focusedElement = page.locator(':focus');
    await expect(focusedElement).toBeVisible();

    // Continue tabbing through elements
    for (let i = 0; i < 5; i++) {
      await page.keyboard.press('Tab');
      const currentFocus = page.locator(':focus');
      await expect(currentFocus).toBeVisible();
    }
  });

  test('should have sufficient color contrast', async ({ page }) => {
    // This is a basic check - full color contrast testing requires specialized tools
    // Check that text is visible against backgrounds
    const textElements = page.locator('p, span, div, h1, h2, h3, h4, h5, h6, button, a');

    for (let i = 0; i < Math.min(await textElements.count(), 10); i++) {
      const element = textElements.nth(i);
      const text = await element.textContent();

      if (text && text.trim().length > 0) {
        await expect(element).toBeVisible();
      }
    }
  });

  test('should have proper form labels', async ({ page }) => {
    await page.goto('/error-reporting');

    // Check that form inputs have labels
    const inputs = page.locator('input, textarea, select');

    for (let i = 0; i < Math.min(await inputs.count(), 5); i++) {
      const input = inputs.nth(i);
      const id = await input.getAttribute('id');
      const ariaLabel = await input.getAttribute('aria-label');
      const ariaLabelledBy = await input.getAttribute('aria-labelledby');

      if (id) {
        // Check for associated label
        const label = page.locator(`label[for="${id}"]`);
        const hasLabel = await label.count() > 0;

        // Input should have label or aria-label
        expect(hasLabel || ariaLabel || ariaLabelledBy).toBeTruthy();
      }
    }
  });

  test('should support screen reader announcements', async ({ page }) => {
    // Check for live regions
    const liveRegions = page.locator('[aria-live], [role="status"], [role="alert"]');

    if (await liveRegions.count() > 0) {
      await expect(liveRegions.first()).toBeVisible();
    }

    // Check for proper announcements on dynamic content
    await page.goto('/verification');

    // Look for status messages that should be announced
    const statusMessages = page.locator('[role="status"], [aria-live="polite"], [aria-live="assertive"]');
    if (await statusMessages.count() > 0) {
      await expect(statusMessages.first()).toBeVisible();
    }
  });

  test('should have proper focus management', async ({ page }) => {
    // Test focus trap in modals
    await page.goto('/admin');

    // Try to open a modal/dialog
    const modalTrigger = page.locator('button:has-text("Details"), button:has-text("Edit"), [data-testid*="open"]');

    if (await modalTrigger.count() > 0) {
      await modalTrigger.click();

      // Check if modal opened
      const modal = page.locator('[role="dialog"], .modal');
      if (await modal.count() > 0) {
        await expect(modal).toBeVisible();

        // Focus should be trapped within modal
        await page.keyboard.press('Tab');
        const focusedElement = page.locator(':focus');

        // Focused element should be within modal
        const isWithinModal = await modal.locator(':focus').count() > 0;
        expect(isWithinModal).toBeTruthy();
      }
    }
  });

  test('should support high contrast mode', async ({ page }) => {
    // Simulate high contrast mode
    await page.emulateMedia({ colorScheme: 'dark' });

    // Check that content is still visible
    await expect(page.locator('body')).toBeVisible();
    await expect(page.locator('h1, h2, h3').first()).toBeVisible();

    // Reset to light mode
    await page.emulateMedia({ colorScheme: 'light' });
    await expect(page.locator('body')).toBeVisible();
  });

  test('should have proper error announcements', async ({ page }) => {
    await page.goto('/error-reporting');

    // Try to submit form without required fields
    const submitButton = page.locator('button[type="submit"], button:has-text("Submit")');

    if (await submitButton.count() > 0) {
      await submitButton.click();

      // Check for error announcements
      const errorMessages = page.locator('[role="alert"], [aria-live="assertive"], .error-message');
      if (await errorMessages.count() > 0) {
        await expect(errorMessages.first()).toBeVisible();
      }
    }
  });

  test('should support zoom up to 200%', async ({ page }) => {
    // Test page at 200% zoom
    await page.setViewportSize({ width: 640, height: 480 }); // Simulate 200% zoom

    // Content should still be accessible
    await expect(page.locator('body')).toBeVisible();
    await expect(page.locator('h1, h2, h3').first()).toBeVisible();

    // Navigation should still work
    const navLinks = page.locator('nav a, .navigation a');
    if (await navLinks.count() > 0) {
      await expect(navLinks.first()).toBeVisible();
    }
  });

  test('should have proper skip links', async ({ page }) => {
    // Check for skip to main content link
    await page.keyboard.press('Tab');

    const skipLink = page.locator('a:has-text("Skip to main"), a:has-text("Skip to content"), .skip-link');

    if (await skipLink.count() > 0) {
      await expect(skipLink).toBeFocused();

      // Skip link should work
      await skipLink.click();

      const mainContent = page.locator('main, [role="main"], #main-content');
      if (await mainContent.count() > 0) {
        await expect(mainContent).toBeFocused();
      }
    }
  });

  test('should have descriptive link text', async ({ page }) => {
    // Check that links have descriptive text
    const links = page.locator('a');

    for (let i = 0; i < Math.min(await links.count(), 10); i++) {
      const link = links.nth(i);
      const text = await link.textContent();
      const ariaLabel = await link.getAttribute('aria-label');
      const title = await link.getAttribute('title');

      // Link should have descriptive text (not just "click here" or "read more")
      const hasDescriptiveText = text && !text.match(/^(click here|read more|more|link)$/i);
      const hasAriaLabel = ariaLabel && ariaLabel.length > 0;
      const hasTitle = title && title.length > 0;

      expect(hasDescriptiveText || hasAriaLabel || hasTitle).toBeTruthy();
    }
  });

  test('should support reduced motion preferences', async ({ page }) => {
    // Simulate reduced motion preference
    await page.emulateMedia({ reducedMotion: 'reduce' });

    // Check that animations are reduced or disabled
    // This is more of a CSS test, but we can check that content is still functional
    await expect(page.locator('body')).toBeVisible();

    // Navigate between pages to ensure transitions work
    await page.goto('/error-reporting');
    await expect(page.locator('text=/error.*report/i')).toBeVisible();

    await page.goto('/verification');
    await expect(page.locator('text=/verification/i')).toBeVisible();
  });
});

import { test, expect } from '@playwright/test';

test.describe('Error Reporting Interface', () => {
  test.beforeEach(async ({ page }) => {
    // Login and navigate to error reporting
    await page.goto('/login');
    await page.locator('input[name="username"]').fill('admin');
    await page.locator('input[name="password"]').fill('AdminPassword123!');
    await page.locator('button[type="submit"]').click();
    await expect(page).toHaveURL(/.*\/dashboard/);

    // Navigate to error reporting page
    await page.goto('/error-reporting');
  });

  test('should display error reporting interface', async ({ page }) => {
    // Check for error reporting elements
    await expect(page.locator('text=/error.*report|report.*error/i')).toBeVisible();

    // Should show text input areas
    await expect(page.locator('textarea, [contenteditable], .text-editor')).toBeVisible();
  });

  test('should support text selection for error reporting', async ({ page }) => {
    // Look for text content that can be selected
    const textArea = page.locator('textarea, [contenteditable], .text-content').first();

    if (await textArea.count() > 0) {
      // Add some sample text
      await textArea.fill('The patient has diabetis and high blod pressure.');

      // Select the word "diabetis"
      await textArea.focus();
      await page.keyboard.press('Control+A'); // Select all

      // Should be able to select text
      await expect(textArea).toBeFocused();
    }
  });

  test('should categorize errors by type', async ({ page }) => {
    // Look for error categorization controls
    const categorySelect = page.locator('select[name*="category"], [aria-label*="category"], .error-category');

    if (await categorySelect.count() > 0) {
      await categorySelect.click();

      // Should show error categories
      await expect(page.locator('option:has-text("Medical Terminology"), option:has-text("Spelling"), option:has-text("Grammar")')).toBeVisible();

      // Select medical terminology
      await page.locator('option:has-text("Medical Terminology")').click();

      // Category should be selected
      await expect(categorySelect).toHaveValue(/medical|terminology/i);
    }
  });

  test('should set error severity levels', async ({ page }) => {
    // Look for severity controls
    const severitySelect = page.locator('select[name*="severity"], [aria-label*="severity"], .severity-level');

    if (await severitySelect.count() > 0) {
      await severitySelect.click();

      // Should show severity options
      await expect(page.locator('option:has-text("Low"), option:has-text("Medium"), option:has-text("High"), option:has-text("Critical")')).toBeVisible();

      // Select high severity
      await page.locator('option:has-text("High")').click();

      // Severity should be selected
      await expect(severitySelect).toHaveValue(/high/i);
    }
  });

  test('should add error descriptions and comments', async ({ page }) => {
    // Find description input
    const descriptionInput = page.locator('textarea[name*="description"], input[name*="description"], [aria-label*="description"]');

    if (await descriptionInput.count() > 0) {
      await descriptionInput.fill('Medical term "diabetis" should be "diabetes". This is a common spelling error that affects medical accuracy.');

      // Verify description was entered
      await expect(descriptionInput).toHaveValue(/Medical term.*diabetis.*should be.*diabetes/);
    }
  });

  test('should submit error reports successfully', async ({ page }) => {
    // Fill out error report form
    const textArea = page.locator('textarea, [contenteditable]').first();
    if (await textArea.count() > 0) {
      await textArea.fill('The patient has diabetis and high blod pressure.');
    }

    // Select category
    const categorySelect = page.locator('select[name*="category"], [aria-label*="category"]');
    if (await categorySelect.count() > 0) {
      await categorySelect.selectOption('Medical Terminology');
    }

    // Select severity
    const severitySelect = page.locator('select[name*="severity"], [aria-label*="severity"]');
    if (await severitySelect.count() > 0) {
      await severitySelect.selectOption('High');
    }

    // Add description
    const descriptionInput = page.locator('textarea[name*="description"], [aria-label*="description"]');
    if (await descriptionInput.count() > 0) {
      await descriptionInput.fill('Spelling error in medical terminology');
    }

    // Submit the report
    const submitButton = page.locator('button:has-text("Submit"), button:has-text("Report Error"), button[type="submit"]');
    if (await submitButton.count() > 0) {
      await submitButton.click();

      // Should show success message
      await expect(page.locator('text=/submitted|success|reported.*successfully/i')).toBeVisible();
    }
  });

  test('should validate required fields', async ({ page }) => {
    // Try to submit without filling required fields
    const submitButton = page.locator('button:has-text("Submit"), button:has-text("Report Error"), button[type="submit"]');

    if (await submitButton.count() > 0) {
      await submitButton.click();

      // Should show validation errors
      await expect(page.locator('text=/required|please.*fill|validation.*error/i')).toBeVisible();
    }
  });

  test('should save error reports as drafts', async ({ page }) => {
    // Fill partial information
    const textArea = page.locator('textarea, [contenteditable]').first();
    if (await textArea.count() > 0) {
      await textArea.fill('Work in progress error report...');
    }

    // Save as draft
    const draftButton = page.locator('button:has-text("Save Draft"), button:has-text("Draft"), [aria-label*="draft"]');
    if (await draftButton.count() > 0) {
      await draftButton.click();

      // Should show draft saved message
      await expect(page.locator('text=/draft.*saved|saved.*draft/i')).toBeVisible();
    }
  });

  test('should support text highlighting and annotation', async ({ page }) => {
    // Add text content
    const textArea = page.locator('textarea, [contenteditable]').first();
    if (await textArea.count() > 0) {
      await textArea.fill('The patient has diabetis and high blod pressure.');

      // Try to select specific text for highlighting
      await textArea.focus();

      // Double-click to select word
      await page.locator('text=/diabetis/i').dblclick();

      // Look for highlighting tools
      const highlightButton = page.locator('button:has-text("Highlight"), .highlight-tool, [aria-label*="highlight"]');
      if (await highlightButton.count() > 0) {
        await highlightButton.click();

        // Text should be highlighted (visual verification)
        await expect(page.locator('.highlighted, .error-highlight')).toBeVisible();
      }
    }
  });

  test('should display error history and previous reports', async ({ page }) => {
    // Look for error history section
    const historySection = page.locator('.error-history, .previous-reports, [data-testid*="history"]');

    if (await historySection.count() > 0) {
      await expect(historySection).toBeVisible();

      // Should show previous error reports
      await expect(page.locator('text=/previous.*reports|error.*history/i')).toBeVisible();
    }
  });

  test('should support error report filtering and search', async ({ page }) => {
    // Look for search functionality
    const searchInput = page.locator('input[type="search"], input[placeholder*="search"], [aria-label*="search"]');

    if (await searchInput.count() > 0) {
      await searchInput.fill('medical terminology');

      // Should filter results
      await expect(page.locator('text=/medical.*terminology/i')).toBeVisible();
    }

    // Look for filter controls
    const filterButton = page.locator('button:has-text("Filter"), select[name*="filter"], [aria-label*="filter"]');
    if (await filterButton.count() > 0) {
      await filterButton.click();

      // Should show filter options
      await expect(page.locator('text=/category|severity|date/i')).toBeVisible();
    }
  });

  test('should handle file attachments for error context', async ({ page }) => {
    // Look for file upload functionality
    const fileInput = page.locator('input[type="file"], .file-upload, [aria-label*="upload"]');

    if (await fileInput.count() > 0) {
      // Create a test file
      const testFile = Buffer.from('Test audio file content');

      // Upload file
      await fileInput.setInputFiles({
        name: 'test-audio.wav',
        mimeType: 'audio/wav',
        buffer: testFile
      });

      // Should show uploaded file
      await expect(page.locator('text=/test-audio\.wav|uploaded.*successfully/i')).toBeVisible();
    }
  });

  test('should provide error reporting analytics', async ({ page }) => {
    // Look for analytics or statistics section
    const analyticsSection = page.locator('.analytics, .statistics, .error-stats, [data-testid*="analytics"]');

    if (await analyticsSection.count() > 0) {
      await expect(analyticsSection).toBeVisible();

      // Should show error metrics
      await expect(page.locator('text=/total.*errors|error.*count|statistics/i')).toBeVisible();
    }
  });

  test('should support collaborative error review', async ({ page }) => {
    // Look for collaboration features
    const reviewSection = page.locator('.error-review, .collaboration, [data-testid*="review"]');

    if (await reviewSection.count() > 0) {
      await expect(reviewSection).toBeVisible();

      // Should show review status
      await expect(page.locator('text=/pending.*review|reviewed|approved/i')).toBeVisible();
    }
  });

  test('should handle error reporting in different languages', async ({ page }) => {
    // Look for language selection
    const languageSelect = page.locator('select[name*="language"], [aria-label*="language"], .language-selector');

    if (await languageSelect.count() > 0) {
      await languageSelect.click();

      // Should show language options
      await expect(page.locator('option:has-text("English"), option:has-text("Spanish"), option:has-text("French")')).toBeVisible();
    }
  });
});

import { test, expect } from '@playwright/test';

test.describe('Speaker Management Interface', () => {
  test.beforeEach(async ({ page }) => {
    // Login and navigate to speaker management
    await page.goto('/login');
    await page.locator('input[name="username"]').fill('admin');
    await page.locator('input[name="password"]').fill('AdminPassword123!');
    await page.locator('button[type="submit"]').click();
    await expect(page).toHaveURL(/.*\/dashboard/);

    // Navigate to speaker management (might be part of admin or separate page)
    await page.goto('/admin');
  });

  test('should display speakers list', async ({ page }) => {
    // Wait for speakers to load
    await page.waitForSelector('text=/speaker|Dr\.|SPEAKER_/i', { timeout: 10000 });

    // Should display speaker information
    await expect(page.locator('text=/Dr.*John.*Smith|SPEAKER_001/i')).toBeVisible();
    await expect(page.locator('text=/Dr.*Sarah.*Johnson|SPEAKER_002/i')).toBeVisible();

    // Should show speaker buckets
    await expect(page.locator('text=/HIGH.*TOUCH|MEDIUM.*TOUCH/i')).toBeVisible();
  });

  test('should support speaker search functionality', async ({ page }) => {
    // Wait for speakers to load
    await page.waitForSelector('text=/speaker|Dr\./i', { timeout: 10000 });

    // Find search input
    const searchInput = page.locator('input[placeholder*="search"], input[type="search"], [aria-label*="search"]');

    if (await searchInput.count() > 0) {
      // Search for specific speaker
      await searchInput.fill('John');

      // Should filter results
      await expect(page.locator('text=/Dr.*John.*Smith/i')).toBeVisible();

      // Clear search
      await searchInput.clear();
      await searchInput.fill('');

      // Should show all speakers again
      await expect(page.locator('text=/Dr.*Sarah.*Johnson/i')).toBeVisible();
    }
  });

  test('should open speaker details dialog', async ({ page }) => {
    // Wait for speakers to load
    await page.waitForSelector('text=/Dr.*John.*Smith/i', { timeout: 10000 });

    // Click on speaker card or row
    const speakerCard = page.locator('[data-testid="speaker-card"], .speaker-card, tr:has-text("Dr. John Smith")').first();

    if (await speakerCard.count() > 0) {
      await speakerCard.click();

      // Should open details dialog
      await expect(page.locator('[role="dialog"], .modal, .speaker-details')).toBeVisible();
      await expect(page.locator('text=/Speaker.*Details|Details/i')).toBeVisible();
      await expect(page.locator('text=/SPEAKER_001/i')).toBeVisible();
    }
  });

  test('should display speaker statistics', async ({ page }) => {
    // Look for statistics section
    const statsSection = page.locator('.statistics, .stats, [data-testid*="stats"]');

    if (await statsSection.count() > 0) {
      await expect(statsSection).toBeVisible();

      // Should show various metrics
      await expect(page.locator('text=/note.*count|notes|recordings/i')).toBeVisible();
      await expect(page.locator('text=/ser.*score|error.*rate/i')).toBeVisible();
    }
  });

  test('should support speaker filtering by bucket', async ({ page }) => {
    // Wait for speakers to load
    await page.waitForSelector('text=/HIGH.*TOUCH|MEDIUM.*TOUCH/i', { timeout: 10000 });

    // Find filter controls
    const filterButton = page.locator('button:has-text("Filter"), select[name*="bucket"], [aria-label*="filter"]');

    if (await filterButton.count() > 0) {
      await filterButton.click();

      // Select HIGH TOUCH filter
      const highTouchOption = page.locator('option:has-text("HIGH TOUCH"), [role="option"]:has-text("HIGH TOUCH")');
      if (await highTouchOption.count() > 0) {
        await highTouchOption.click();

        // Should show only HIGH TOUCH speakers
        await expect(page.locator('text=/HIGH.*TOUCH/i')).toBeVisible();
      }
    }
  });

  test('should support speaker bucket transitions', async ({ page }) => {
    // Wait for speakers to load
    await page.waitForSelector('text=/Dr.*John.*Smith/i', { timeout: 10000 });

    // Open speaker details
    const speakerCard = page.locator('[data-testid="speaker-card"], .speaker-card, tr:has-text("Dr. John Smith")').first();

    if (await speakerCard.count() > 0) {
      await speakerCard.click();

      // Look for bucket transition controls
      const transitionButton = page.locator('button:has-text("Move to"), button:has-text("Transition"), select[name*="bucket"]');

      if (await transitionButton.count() > 0) {
        await transitionButton.click();

        // Select new bucket
        const newBucketOption = page.locator('option:has-text("MEDIUM TOUCH"), [role="option"]:has-text("MEDIUM")');
        if (await newBucketOption.count() > 0) {
          await newBucketOption.click();

          // Should show confirmation or success message
          await expect(page.locator('text=/moved|transition.*successful|updated/i')).toBeVisible();
        }
      }
    }
  });

  test('should display speaker performance metrics', async ({ page }) => {
    // Open speaker details
    await page.waitForSelector('text=/Dr.*John.*Smith/i', { timeout: 10000 });
    const speakerCard = page.locator('[data-testid="speaker-card"], .speaker-card, tr:has-text("Dr. John Smith")').first();

    if (await speakerCard.count() > 0) {
      await speakerCard.click();

      // Should show performance metrics
      await expect(page.locator('text=/performance|metrics|statistics/i')).toBeVisible();
      await expect(page.locator('text=/error.*rate|accuracy|quality/i')).toBeVisible();
    }
  });

  test('should support data grid interactions', async ({ page }) => {
    // Wait for data grid to load
    await page.waitForSelector('[role="grid"], .data-grid, table', { timeout: 10000 });

    const dataGrid = page.locator('[role="grid"], .data-grid, table').first();

    if (await dataGrid.count() > 0) {
      // Test column sorting
      const sortableHeader = page.locator('th[role="columnheader"], .sortable-header').first();
      if (await sortableHeader.count() > 0) {
        await sortableHeader.click();

        // Should sort the data (visual verification needed)
        await expect(dataGrid).toBeVisible();
      }

      // Test row selection
      const firstRow = page.locator('tr[role="row"], tbody tr').first();
      if (await firstRow.count() > 0) {
        await firstRow.click();

        // Row should be selected (visual verification)
        await expect(firstRow).toBeVisible();
      }
    }
  });

  test('should handle pagination in speaker list', async ({ page }) => {
    // Look for pagination controls
    const paginationNext = page.locator('button:has-text("Next"), [aria-label*="next page"]');
    const paginationPrev = page.locator('button:has-text("Previous"), [aria-label*="previous page"]');

    if (await paginationNext.count() > 0) {
      await paginationNext.click();

      // Should load next page
      await expect(page.locator('text=/speaker|Dr\./i')).toBeVisible();

      if (await paginationPrev.count() > 0) {
        await paginationPrev.click();

        // Should go back to previous page
        await expect(page.locator('text=/speaker|Dr\./i')).toBeVisible();
      }
    }
  });

  test('should export speaker data', async ({ page }) => {
    // Look for export functionality
    const exportButton = page.locator('button:has-text("Export"), button:has-text("Download"), [aria-label*="export"]');

    if (await exportButton.count() > 0) {
      // Set up download handler
      const downloadPromise = page.waitForEvent('download');

      await exportButton.click();

      // Should trigger download
      const download = await downloadPromise;
      expect(download.suggestedFilename()).toMatch(/\.csv|\.xlsx|\.json/);
    }
  });

  test('should validate speaker data integrity', async ({ page }) => {
    // Wait for speakers to load
    await page.waitForSelector('text=/Dr.*John.*Smith/i', { timeout: 10000 });

    // Check that speaker IDs are properly formatted
    await expect(page.locator('text=/SPEAKER_\d{3}/i')).toBeVisible();

    // Check that bucket assignments are valid
    await expect(page.locator('text=/HIGH.*TOUCH|MEDIUM.*TOUCH|LOW.*TOUCH/i')).toBeVisible();

    // Check that metrics are displayed as numbers
    const metricElements = page.locator('text=/\d+.*notes|\d+.*%|\d+\.\d+/i');
    if (await metricElements.count() > 0) {
      await expect(metricElements.first()).toBeVisible();
    }
  });

  test('should handle speaker management errors gracefully', async ({ page }) => {
    // Try to perform an action that might fail
    const speakerCard = page.locator('[data-testid="speaker-card"], .speaker-card').first();

    if (await speakerCard.count() > 0) {
      // Simulate network error by intercepting requests
      await page.route('**/api/**', route => route.abort());

      await speakerCard.click();

      // Should show error message
      await expect(page.locator('text=/error|failed|unable.*to.*load/i')).toBeVisible();
    }
  });
});

import { Page, expect } from '@playwright/test';

/**
 * Common test utilities for Playwright E2E tests
 */

export class TestHelpers {
  constructor(private page: Page) {}

  /**
   * Login with provided credentials
   */
  async login(username: string = 'admin', password: string = 'AdminPassword123!') {
    await this.page.goto('/login');
    await this.page.locator('input[name="username"]').fill(username);
    await this.page.locator('input[name="password"]').fill(password);
    await this.page.locator('button[type="submit"]').click();
    await expect(this.page).toHaveURL(/.*\/dashboard/);
  }

  /**
   * Wait for loading to complete
   */
  async waitForLoading() {
    // Wait for loading spinners to disappear
    await this.page.waitForSelector('.loading, [data-testid="loading"]', { state: 'hidden', timeout: 10000 });
  }

  /**
   * Navigate to a specific page and wait for it to load
   */
  async navigateAndWait(path: string) {
    await this.page.goto(path);
    await this.waitForLoading();
  }

  /**
   * Fill form field by various selectors
   */
  async fillField(fieldName: string, value: string) {
    const selectors = [
      `input[name="${fieldName}"]`,
      `textarea[name="${fieldName}"]`,
      `select[name="${fieldName}"]`,
      `[aria-label*="${fieldName}" i]`,
      `[placeholder*="${fieldName}" i]`
    ];

    for (const selector of selectors) {
      const element = this.page.locator(selector);
      if (await element.count() > 0) {
        await element.fill(value);
        return;
      }
    }

    throw new Error(`Could not find field: ${fieldName}`);
  }

  /**
   * Click button by text or aria-label
   */
  async clickButton(buttonText: string) {
    const selectors = [
      `button:has-text("${buttonText}")`,
      `[role="button"]:has-text("${buttonText}")`,
      `button[aria-label*="${buttonText}" i]`,
      `[aria-label*="${buttonText}" i][role="button"]`
    ];

    for (const selector of selectors) {
      const element = this.page.locator(selector);
      if (await element.count() > 0) {
        await element.click();
        return;
      }
    }

    throw new Error(`Could not find button: ${buttonText}`);
  }

  /**
   * Wait for and verify success message
   */
  async expectSuccessMessage(message?: string) {
    const successSelectors = [
      'text=/success|successful|saved|submitted/i',
      '[role="alert"]:has-text(/success/i)',
      '.success-message, .alert-success'
    ];

    if (message) {
      await expect(this.page.locator(`text=/${message}/i`)).toBeVisible();
    } else {
      for (const selector of successSelectors) {
        const element = this.page.locator(selector);
        if (await element.count() > 0) {
          await expect(element).toBeVisible();
          return;
        }
      }
    }
  }

  /**
   * Wait for and verify error message
   */
  async expectErrorMessage(message?: string) {
    const errorSelectors = [
      'text=/error|failed|invalid|required/i',
      '[role="alert"]:has-text(/error/i)',
      '.error-message, .alert-error'
    ];

    if (message) {
      await expect(this.page.locator(`text=/${message}/i`)).toBeVisible();
    } else {
      for (const selector of errorSelectors) {
        const element = this.page.locator(selector);
        if (await element.count() > 0) {
          await expect(element).toBeVisible();
          return;
        }
      }
    }
  }

  /**
   * Select option from dropdown
   */
  async selectOption(selectName: string, optionValue: string) {
    const selectors = [
      `select[name="${selectName}"]`,
      `[aria-label*="${selectName}" i]`,
      `[data-testid*="${selectName}"]`
    ];

    for (const selector of selectors) {
      const element = this.page.locator(selector);
      if (await element.count() > 0) {
        await element.selectOption(optionValue);
        return;
      }
    }

    throw new Error(`Could not find select: ${selectName}`);
  }

  /**
   * Upload file to file input
   */
  async uploadFile(fileName: string, content: Buffer, mimeType: string) {
    const fileInput = this.page.locator('input[type="file"]');
    await fileInput.setInputFiles({
      name: fileName,
      mimeType: mimeType,
      buffer: content
    });
  }

  /**
   * Take screenshot with timestamp
   */
  async takeScreenshot(name: string) {
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    await this.page.screenshot({ 
      path: `screenshots/${name}-${timestamp}.png`,
      fullPage: true 
    });
  }

  /**
   * Check if element exists without throwing
   */
  async elementExists(selector: string): Promise<boolean> {
    return await this.page.locator(selector).count() > 0;
  }

  /**
   * Wait for network requests to complete
   */
  async waitForNetworkIdle() {
    await this.page.waitForLoadState('networkidle');
  }

  /**
   * Simulate keyboard shortcuts
   */
  async pressShortcut(shortcut: string) {
    await this.page.keyboard.press(shortcut);
  }

  /**
   * Verify page accessibility basics
   */
  async checkBasicAccessibility() {
    // Check for h1
    await expect(this.page.locator('h1')).toHaveCount({ min: 1 });

    // Check for main landmark
    const main = this.page.locator('main, [role="main"]');
    if (await main.count() > 0) {
      await expect(main).toBeVisible();
    }

    // Check for navigation
    const nav = this.page.locator('nav, [role="navigation"]');
    if (await nav.count() > 0) {
      await expect(nav).toBeVisible();
    }
  }

  /**
   * Mock API responses for testing
   */
  async mockApiResponse(url: string, response: any) {
    await this.page.route(url, route => {
      route.fulfill({
        status: 200,
        contentType: 'application/json',
        body: JSON.stringify(response)
      });
    });
  }

  /**
   * Simulate network error
   */
  async simulateNetworkError(urlPattern: string) {
    await this.page.route(urlPattern, route => route.abort());
  }
}

