        />
        <CardContent>
          {[1, 2, 3].map((i) => (
            <Box key={i} sx={{ mb: 2 }}>
              <Skeleton variant="text" width="100%" />
              <Skeleton variant="rectangular" width="100%" height={8} />
            </Box>
          ))}
        </CardContent>
      </Card>
    );
  }

  if (error) {
    return (
      <Card sx={{ height: '100%' }}>
        <CardHeader title="MT Validation Metrics" />
        <CardContent>
          <Alert severity="error">{error}</Alert>
        </CardContent>
      </Card>
    );
  }

  if (!data) {
    return (
      <Card sx={{ height: '100%' }}>
        <CardHeader title="MT Validation Metrics" />
        <CardContent>
          <Alert severity="info">No MT validation data available</Alert>
        </CardContent>
      </Card>
    );
  }

  const completionRate = (data.statistics.completed_sessions / data.statistics.total_sessions) * 100;

  return (
    <Card sx={{ height: '100%' }}>
      <CardHeader
        title="MT Validation Metrics"
        subheader={`${data.statistics.total_sessions} total sessions`}
        action={
          onRefresh && (
            <Tooltip title="Refresh">
              <IconButton onClick={onRefresh} size="small">
                <RefreshIcon />
              </IconButton>
            </Tooltip>
          )
        }
      />

      <CardContent>
        {/* Active Sessions */}
        <Box sx={{ mb: 3, textAlign: 'center' }}>
          <Typography variant="h3" fontWeight="bold" color="primary.main">
            {data.statistics.active_sessions}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Active Sessions
          </Typography>
          {data.statistics.active_sessions > 0 && (
            <Chip
              icon={<ActiveIcon />}
              label="In Progress"
              color="primary"
              size="small"
              sx={{ mt: 1 }}
            />
          )}
        </Box>

        {/* Session Statistics */}
        <Grid container spacing={2} sx={{ mb: 2 }}>
          <Grid item xs={6}>
            <Box sx={{ textAlign: 'center' }}>
              <Typography variant="h6" fontWeight="bold" color="success.main">
                {data.statistics.completed_sessions}
              </Typography>
              <Typography variant="caption" color="text.secondary">
                Completed
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={6}>
            <Box sx={{ textAlign: 'center' }}>
              <Typography variant="h6" fontWeight="bold" color="info.main">
                {data.statistics.total_feedback_items.toLocaleString()}
              </Typography>
              <Typography variant="caption" color="text.secondary">
                Feedback Items
              </Typography>
            </Box>
          </Grid>
        </Grid>

        {/* Completion Rate */}
        <Box sx={{ mb: 2 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
            <Typography variant="body2" color="text.secondary">
              Completion Rate
            </Typography>
            <Typography variant="body2" fontWeight="medium">
              {completionRate.toFixed(1)}%
            </Typography>
          </Box>
          <LinearProgress
            variant="determinate"
            value={completionRate}
            color={completionRate > 90 ? 'success' : completionRate > 70 ? 'warning' : 'error'}
            sx={{ height: 8, borderRadius: 4 }}
          />
        </Box>

        {/* Average Rating */}
        <Box sx={{ mb: 2 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
            <RatingIcon fontSize="small" color="action" />
            <Typography variant="body2" color="text.secondary">
              Average Rating
            </Typography>
          </Box>
          <Typography variant="h6" fontWeight="bold" color="warning.main">
            {data.quality_metrics.average_rating.toFixed(1)}/5
          </Typography>
        </Box>

        {/* MT Users */}
        <Box sx={{ mb: 2 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
            <UserIcon fontSize="small" color="action" />
            <Typography variant="body2" color="text.secondary">
              Active MT Users
            </Typography>
          </Box>
          <Typography variant="h6" fontWeight="bold">
            {data.productivity.mt_user_count}
          </Typography>
        </Box>

        {/* Productivity Metrics */}
        <Box sx={{ mb: 2 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
            <ProductivityIcon fontSize="small" color="action" />
            <Typography variant="body2" color="text.secondary">
              Items per Hour
            </Typography>
          </Box>
          <Typography variant="h6" fontWeight="bold">
            {data.productivity.items_per_hour.toFixed(1)}
          </Typography>
        </Box>

        {/* Bucket Recommendations */}
        <Box>
          <Typography variant="body2" color="text.secondary" gutterBottom>
            Bucket Change Recommendations
          </Typography>
          <Typography variant="h6" fontWeight="bold" color="secondary.main">
            {data.quality_metrics.bucket_change_recommendations}
          </Typography>
        </Box>

        {/* Status Indicators */}
        <Box sx={{ mt: 2, display: 'flex', gap: 1, flexWrap: 'wrap' }}>
          {data.quality_metrics.average_rating > 4 && (
            <Chip
              icon={<RatingIcon />}
              label="High Quality"
              color="success"
              size="small"
              variant="outlined"
            />
          )}

          {data.productivity.efficiency_score > 85 && (
            <Chip
              icon={<ProductivityIcon />}
              label="High Efficiency"
              color="info"
              size="small"
              variant="outlined"
            />
          )}

          {data.quality_metrics.bucket_change_recommendations > 0 && (
            <Chip
              label="Pending Recommendations"
              color="warning"
              size="small"
              variant="outlined"
            />
          )}
        </Box>
      </CardContent>
    </Card>
  );
};

export default MTValidationMetrics;

/**
 * Quick Metrics Bar Component
 * 
 * Horizontal bar displaying key metrics with trends
 * and status indicators.
 */

import React from 'react';
import {
  Box,
  Paper,
  Grid,
  Typography,
  Chip,
  Skeleton,
  Icon,
} from '@mui/material';
import {
  TrendingUp as TrendUpIcon,
  TrendingDown as TrendDownIcon,
  TrendingFlat as TrendFlatIcon,
  People as PeopleIcon,
  Assessment as AssessmentIcon,
  PlayArrow as PlayIcon,
  SwapHoriz as SwapIcon,
} from '@mui/icons-material';
import { QuickMetric } from '@/domain/types/dashboard';

interface QuickMetricsBarProps {
  metrics: QuickMetric[];
  loading?: boolean;
}

interface MetricCardProps {
  metric: QuickMetric;
}

const MetricCard: React.FC<MetricCardProps> = ({ metric }) => {
  const getIcon = (iconName?: string) => {
    switch (iconName) {
      case 'people': return <PeopleIcon />;
      case 'assessment': return <AssessmentIcon />;
      case 'play_arrow': return <PlayIcon />;
      case 'swap_horiz': return <SwapIcon />;
      default: return <AssessmentIcon />;
    }
  };

  const getTrendIcon = (direction?: 'up' | 'down' | 'stable') => {
    switch (direction) {
      case 'up': return <TrendUpIcon color="success" fontSize="small" />;
      case 'down': return <TrendDownIcon color="error" fontSize="small" />;
      case 'stable': return <TrendFlatIcon color="info" fontSize="small" />;
      default: return null;
    }
  };

  const getStatusColor = (status?: 'good' | 'warning' | 'critical') => {
    switch (status) {
      case 'good': return 'success';
      case 'warning': return 'warning';
      case 'critical': return 'error';
      default: return 'primary';
    }
  };

  return (
    <Paper
      elevation={1}
      sx={{
        p: 2,
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        borderLeft: 4,
        borderLeftColor: `${metric.color || 'primary'}.main`,
        transition: 'all 0.2s ease-in-out',
        '&:hover': {
          elevation: 3,
          transform: 'translateY(-2px)',
        },
      }}
    >
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 1 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Box sx={{ color: `${metric.color || 'primary'}.main` }}>
            {getIcon(metric.icon)}
          </Box>
          <Typography variant="body2" color="text.secondary" fontWeight="medium">
            {metric.label}
          </Typography>
        </Box>

        {metric.status && (
          <Chip
            size="small"
            label={metric.status.toUpperCase()}
            color={getStatusColor(metric.status)}
            variant="outlined"
          />
        )}
      </Box>

      <Box sx={{ display: 'flex', alignItems: 'baseline', gap: 1, mb: 1 }}>
        <Typography variant="h4" fontWeight="bold" color={`${metric.color || 'primary'}.main`}>
          {metric.value}
        </Typography>
        {metric.unit && (
          <Typography variant="body2" color="text.secondary">
            {metric.unit}
          </Typography>
        )}
      </Box>

      {metric.trend && (
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          {getTrendIcon(metric.trend.direction)}
          <Typography
            variant="caption"
            color={
              metric.trend.direction === 'up' ? 'success.main' :
              metric.trend.direction === 'down' ? 'error.main' : 'text.secondary'
            }
            fontWeight="medium"
          >
            {metric.trend.direction === 'up' ? '+' : metric.trend.direction === 'down' ? '-' : ''}
            {metric.trend.percentage.toFixed(1)}% {metric.trend.period}
          </Typography>
        </Box>
      )}
    </Paper>
  );
};

const MetricSkeleton: React.FC = () => (
  <Paper elevation={1} sx={{ p: 2, height: '100%' }}>
    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
      <Skeleton variant="circular" width={24} height={24} />
      <Skeleton variant="text" width={100} />
    </Box>
    <Skeleton variant="text" width={80} height={40} sx={{ mb: 1 }} />
    <Skeleton variant="text" width={120} height={20} />
  </Paper>
);

export const QuickMetricsBar: React.FC<QuickMetricsBarProps> = ({
  metrics,
  loading = false,
}) => {
  if (loading) {
    return (
      <Box sx={{ p: 2, backgroundColor: 'background.default' }}>
        <Grid container spacing={2}>
          {[1, 2, 3, 4].map((index) => (
            <Grid item xs={12} sm={6} md={3} key={index}>
              <MetricSkeleton />
            </Grid>
          ))}
        </Grid>
      </Box>
    );
  }

  if (!metrics || metrics.length === 0) {
    return (
      <Box sx={{ p: 2, backgroundColor: 'background.default' }}>
        <Paper elevation={1} sx={{ p: 3, textAlign: 'center' }}>
          <Typography variant="body2" color="text.secondary">
            No metrics available
          </Typography>
        </Paper>
      </Box>
    );
  }

  return (
    <Box sx={{ p: 2, backgroundColor: 'background.default' }}>
      <Grid container spacing={2}>
        {metrics.map((metric, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <MetricCard metric={metric} />
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default QuickMetricsBar;

