  <Box sx={{ display: 'flex', gap: 0.5, alignItems: 'center' }}>
    {keys.map((key, index) => (
      <React.Fragment key={key}>
        {index > 0 && (
          <Typography variant="caption" color="text.secondary" sx={{ mx: 0.5 }}>
            +
          </Typography>
