          label={key}
          size="small"
          variant="outlined"
          sx={{
            fontFamily: 'monospace',
            fontSize: '0.75rem',
            height: 24,
            minWidth: 24,
            '& .MuiChip-label': {
              px: 1,
            },
          }}
        />
      </React.Fragment>
