);

export const KeyboardShortcutsHelper: React.FC<KeyboardShortcutsHelperProps> = ({
  open,
  onClose,
}) => {
  const categories = Array.from(new Set(shortcuts.map(s => s.category)));

  return (
    <Dialog
      open={open}
      onClose={onClose}
      maxWidth="md"
      fullWidth
      PaperProps={{
        sx: { maxHeight: '80vh' }
