      <DialogContent>
        <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
          Use these keyboard shortcuts to navigate and interact with the MT validation interface more efficiently.
        </Typography>

        {categories.map((category, categoryIndex) => (
          <Box key={category} sx={{ mb: 3 }}>
            <Typography variant="h6" gutterBottom color="primary">
              {category}
            </Typography>

            <Card variant="outlined">
              <CardContent sx={{ py: 2 }}>
                <Grid container spacing={2}>
                  {shortcuts
                    .filter(shortcut => shortcut.category === category)
                    .map((shortcut, index) => (
                      <Grid item xs={12} key={index}>
                        <Box sx={{ 
                          display: 'flex', 
                          alignItems: 'center', 
                          justifyContent: 'space-between',
                          py: 1,
                          borderBottom: index < shortcuts.filter(s => s.category === category).length - 1 
                            ? '1px solid' 
                            : 'none',
                          borderColor: 'divider',
                        }}>
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                            {shortcut.icon && (
                              <Box sx={{ color: 'text.secondary' }}>
                                {shortcut.icon}
                              </Box>
                            )}
                            <Typography variant="body2">
                              {shortcut.description}
                            </Typography>
                          </Box>
                          <KeyShortcut keys={shortcut.keys} />
                        </Box>
                      </Grid>
                    ))}
                </Grid>
              </CardContent>
            </Card>
          </Box>
        ))}

        <Divider sx={{ my: 3 }} />

        {/* Tips */}
        <Box>
          <Typography variant="h6" gutterBottom color="primary">
            Tips
          </Typography>
          <Card variant="outlined" sx={{ backgroundColor: 'background.default' }}>
            <CardContent>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                <Typography variant="body2">
                   Keyboard shortcuts work when not typing in text fields
                </Typography>
                <Typography variant="body2">
                   Use Tab to navigate between form fields
                </Typography>
                <Typography variant="body2">
                   Press Escape to cancel any ongoing action
                </Typography>
                <Typography variant="body2">
                   Shortcuts are case-insensitive (N and n both work)
                </Typography>
                <Typography variant="body2">
                   Hold Shift with navigation keys for bulk selection
                </Typography>
              </Box>
            </CardContent>
          </Card>
        </Box>

        {/* Browser Compatibility */}
        <Box sx={{ mt: 3 }}>
          <Typography variant="caption" color="text.secondary">
            <strong>Note:</strong> Some shortcuts may conflict with browser shortcuts. 
            Use F11 for browser fullscreen if F key doesn't work for application fullscreen.
          </Typography>
        </Box>
      </DialogContent>

      <DialogActions>
        <Button onClick={onClose} variant="contained">
          Got it
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default KeyboardShortcutsHelper;

/**
 * MT Validation Interface Component
 * 
 * Main interface for medical transcriptionist validation workflow
 * with side-by-side text comparison, difference highlighting, and feedback controls.
 */

import React, { useEffect, useState, useCallback } from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  Card,
  CardContent,
  CardHeader,
  Divider,
  IconButton,
  Tooltip,
  Chip,
  LinearProgress,
  Alert,
  Fab,
  SpeedDial,
  SpeedDialAction,
  SpeedDialIcon,
} from '@mui/material';
import {
  NavigateNext as NextIcon,
  NavigateBefore as PrevIcon,
  Visibility as ViewIcon,
  VisibilityOff as HideIcon,
  CompareArrows as CompareIcon,
  Assessment as MetricsIcon,
  Save as SaveIcon,
  Settings as SettingsIcon,
  Fullscreen as FullscreenIcon,
  FullscreenExit as FullscreenExitIcon,
  KeyboardArrowUp as KeyboardArrowUpIcon,
} from '@mui/icons-material';
import { useAppDispatch, useAppSelector } from '@/app/hooks';
import {
  selectCurrentSession,
  selectCurrentItem,
  selectCurrentItemIndex,
  selectTestDataItems,
  selectSessionProgress,
  selectComparisonMode,
  selectShowDifferences,
  selectShowSERMetrics,
  selectTextDifferences,
  selectValidationLoading,
  selectUserPreferences,
  nextItem,
  previousItem,
  setComparisonMode,
  toggleDifferences,
  toggleSERMetrics,
  calculateTextDifferences,
} from '../mt-validation-slice';

// Sub-components
import TextComparisonPanel from './TextComparisonPanel';
import SERMetricsPanel from './SERMetricsPanel';
import FeedbackPanel from './FeedbackPanel';
import ValidationProgress from './ValidationProgress';
import KeyboardShortcutsHelper from './KeyboardShortcutsHelper';

interface MTValidationInterfaceProps {
  sessionId: string;
  onComplete?: () => void;
  onPause?: () => void;
  compact?: boolean;
}

export const MTValidationInterface: React.FC<MTValidationInterfaceProps> = ({
  sessionId,
  onComplete,
  onPause,
  compact = false,
}) => {
  const dispatch = useAppDispatch();

  // Redux state
  const currentSession = useAppSelector(selectCurrentSession);
  const currentItem = useAppSelector(selectCurrentItem);
  const currentItemIndex = useAppSelector(selectCurrentItemIndex);
  const testDataItems = useAppSelector(selectTestDataItems);
  const sessionProgress = useAppSelector(selectSessionProgress);
  const comparisonMode = useAppSelector(selectComparisonMode);
  const showDifferences = useAppSelector(selectShowDifferences);
  const showSERMetrics = useAppSelector(selectShowSERMetrics);
  const textDifferences = useAppSelector(selectTextDifferences);
  const loading = useAppSelector(selectValidationLoading);
  const userPreferences = useAppSelector(selectUserPreferences);

  // Local state
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [speedDialOpen, setSpeedDialOpen] = useState(false);
  const [showKeyboardHelp, setShowKeyboardHelp] = useState(false);

  // Calculate text differences when current item changes
  useEffect(() => {
    if (currentItem) {
      dispatch(calculateTextDifferences({
        originalText: currentItem.original_asr_text,
        correctedText: currentItem.rag_corrected_text,
      }));
    }
  }, [dispatch, currentItem]);

  // Keyboard shortcuts
  useEffect(() => {
    if (!userPreferences.keyboard_shortcuts_enabled) return;

    const handleKeyPress = (event: KeyboardEvent) => {
      // Don't trigger shortcuts when typing in input fields
      if (event.target instanceof HTMLInputElement || event.target instanceof HTMLTextAreaElement) {
        return;
      }

      switch (event.key) {
        case 'ArrowRight':
        case 'n':
          event.preventDefault();
          handleNextItem();
          break;
        case 'ArrowLeft':
        case 'p':
          event.preventDefault();
          handlePreviousItem();
          break;
        case 'd':
          event.preventDefault();
          dispatch(toggleDifferences());
          break;
        case 'm':
          event.preventDefault();
          dispatch(toggleSERMetrics());
          break;
        case 'f':
          event.preventDefault();
          toggleFullscreen();
          break;
        case '?':
          event.preventDefault();
          setShowKeyboardHelp(true);
          break;
        case 'Escape':
          setShowKeyboardHelp(false);
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [dispatch, userPreferences.keyboard_shortcuts_enabled]);

  // Navigation handlers
  const handleNextItem = useCallback(() => {
    if (currentItemIndex < testDataItems.length - 1) {
      dispatch(nextItem());
    }
  }, [dispatch, currentItemIndex, testDataItems.length]);

  const handlePreviousItem = useCallback(() => {
    if (currentItemIndex > 0) {
      dispatch(previousItem());
    }
  }, [dispatch, currentItemIndex]);

  // UI handlers
  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen();
      setIsFullscreen(true);
    } else {
      document.exitFullscreen();
      setIsFullscreen(false);
    }
  };

  const handleComparisonModeChange = (mode: 'side-by-side' | 'unified' | 'overlay') => {
    dispatch(setComparisonMode(mode));
  };

  // Speed dial actions
  const speedDialActions = [
    {
      icon: <SettingsIcon />,
      name: 'Settings',
      onClick: () => {
        // Open settings dialog
      },
    },
    {
      icon: showDifferences ? <HideIcon /> : <ViewIcon />,
      name: showDifferences ? 'Hide Differences' : 'Show Differences',
      onClick: () => dispatch(toggleDifferences()),
    },
    {
      icon: <MetricsIcon />,
      name: 'Toggle SER Metrics',
      onClick: () => dispatch(toggleSERMetrics()),
    },
    {
      icon: isFullscreen ? <FullscreenExitIcon /> : <FullscreenIcon />,
      name: isFullscreen ? 'Exit Fullscreen' : 'Fullscreen',
      onClick: toggleFullscreen,
    },
  ];

  if (!currentSession || !currentItem) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: 400 }}>
        <Typography variant="h6" color="text.secondary">
          No validation session or item selected
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ height: '100vh', display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>
      {/* Header */}
      <Paper elevation={1} sx={{ p: 2, borderRadius: 0 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          {/* Session Info */}
          <Box>
            <Typography variant="h6" component="h1">
              {currentSession.session_name}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Item {currentItemIndex + 1} of {testDataItems.length}
            </Typography>
          </Box>

          {/* Controls */}
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            {/* Comparison Mode Toggle */}
            <Tooltip title="Comparison Mode">
              <IconButton
                onClick={() => {
                  const modes: Array<'side-by-side' | 'unified' | 'overlay'> = ['side-by-side', 'unified', 'overlay'];
                  const currentIndex = modes.indexOf(comparisonMode);
                  const nextMode = modes[(currentIndex + 1) % modes.length];
                  handleComparisonModeChange(nextMode);
                }}
              >
                <CompareIcon />
              </IconButton>
            </Tooltip>

            {/* Differences Toggle */}
            <Tooltip title={showDifferences ? 'Hide Differences' : 'Show Differences'}>
              <IconButton onClick={() => dispatch(toggleDifferences())}>
                {showDifferences ? <HideIcon /> : <ViewIcon />}
              </IconButton>
            </Tooltip>

            {/* SER Metrics Toggle */}
            <Tooltip title="Toggle SER Metrics">
              <IconButton onClick={() => dispatch(toggleSERMetrics())}>
                <MetricsIcon color={showSERMetrics ? 'primary' : 'default'} />
              </IconButton>
            </Tooltip>

            {/* Navigation */}
            <Tooltip title="Previous Item ()">
              <span>
                <IconButton
                  onClick={handlePreviousItem}
                  disabled={currentItemIndex === 0}
                >
                  <PrevIcon />
                </IconButton>
              </span>
            </Tooltip>

            <Chip
              label={`${currentItemIndex + 1} / ${testDataItems.length}`}
              size="small"
              variant="outlined"
            />

            <Tooltip title="Next Item ()">
              <span>
                <IconButton
                  onClick={handleNextItem}
                  disabled={currentItemIndex === testDataItems.length - 1}
                >
                  <NextIcon />
                </IconButton>
              </span>
            </Tooltip>
          </Box>
        </Box>

        {/* Progress Bar */}
        <Box sx={{ mt: 1 }}>
          <ValidationProgress
            current={currentItemIndex + 1}
            total={testDataItems.length}
            progress={sessionProgress}
            compact={true}
          />
        </Box>
      </Paper>

      {/* Main Content */}
      <Box sx={{ flex: 1, display: 'flex', overflow: 'hidden' }}>
        {/* Text Comparison Panel */}
        <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>
          <TextComparisonPanel
            originalText={currentItem.original_asr_text}
            correctedText={currentItem.rag_corrected_text}
            referenceText={currentItem.final_reference_text}
            differences={textDifferences}
            comparisonMode={comparisonMode}
            showDifferences={showDifferences}
            loading={loading.differences}
          />
        </Box>

        {/* Side Panel */}
        <Box sx={{ width: 400, display: 'flex', flexDirection: 'column', borderLeft: 1, borderColor: 'divider' }}>
          {/* SER Metrics Panel */}
          {showSERMetrics && (
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
              <SERMetricsPanel
                originalMetrics={currentItem.original_ser_metrics}
                correctedMetrics={currentItem.corrected_ser_metrics}
                improvementMetrics={currentItem.improvement_metrics}
                compact={true}
              />
            </Box>
          )}

          {/* Feedback Panel */}
          <Box sx={{ flex: 1, overflow: 'auto' }}>
            <FeedbackPanel
              sessionId={sessionId}
              currentItem={currentItem}
              onFeedbackSubmitted={handleNextItem}
              autoAdvance={userPreferences.auto_advance}
            />
          </Box>
        </Box>
      </Box>

      {/* Speed Dial */}
      <SpeedDial
        ariaLabel="Validation actions"
        sx={{ position: 'fixed', bottom: 16, right: 16 }}
        icon={<SpeedDialIcon />}
        onClose={() => setSpeedDialOpen(false)}
        onOpen={() => setSpeedDialOpen(true)}
        open={speedDialOpen}
      >
        {speedDialActions.map((action) => (
          <SpeedDialAction
            key={action.name}
            icon={action.icon}
            tooltipTitle={action.name}
            onClick={() => {
              action.onClick();
              setSpeedDialOpen(false);
            }}
          />
        ))}
      </SpeedDial>

      {/* Keyboard Shortcuts Helper */}
      {showKeyboardHelp && (
        <KeyboardShortcutsHelper
          open={showKeyboardHelp}
          onClose={() => setShowKeyboardHelp(false)}
        />
      )}

      {/* Loading Overlay */}
      {loading.testData && (
        <Box
          sx={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 9999,
          }}
        >
          <Paper sx={{ p: 3, textAlign: 'center' }}>
            <LinearProgress sx={{ mb: 2 }} />
            <Typography variant="body1">Loading validation data...</Typography>
          </Paper>
        </Box>
      )}
    </Box>
  );
};

export default MTValidationInterface;

/**
 * SER Metrics Panel Component
 * 
 * Displays SER metrics comparison with visual indicators
 * and improvement analysis for MT validation.
 */

import React from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  Card,
  CardContent,
  LinearProgress,
  Chip,
  Divider,
  Tooltip,
  IconButton,
  Collapse,
} from '@mui/material';
import {
  TrendingUp as ImprovementIcon,
  TrendingDown as DegradationIcon,
  TrendingFlat as NoChangeIcon,
  Info as InfoIcon,
  ExpandMore as ExpandMoreIcon,
  ExpandLess as ExpandLessIcon,
} from '@mui/icons-material';
import { SERMetrics, SERComparison } from '@/domain/types/mt-validation';

interface SERMetricsPanelProps {
  originalMetrics: SERMetrics;
  correctedMetrics: SERMetrics;
  improvementMetrics: SERComparison;
  compact?: boolean;
}

interface MetricDisplayProps {
  label: string;
  value: number;
  unit?: string;
  color?: 'primary' | 'secondary' | 'error' | 'warning' | 'info' | 'success';
  showProgress?: boolean;
  maxValue?: number;
  tooltip?: string;
}

const MetricDisplay: React.FC<MetricDisplayProps> = ({
  label,
  value,
  unit = '',
  color = 'primary',
  showProgress = false,
  maxValue = 100,
  tooltip,
}) => {
  const displayValue = typeof value === 'number' ? value.toFixed(1) : value;

  return (
    <Box sx={{ mb: 1 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 0.5 }}>
        <Typography variant="body2" color="text.secondary">
          {label}
          {tooltip && (
            <Tooltip title={tooltip}>
              <InfoIcon sx={{ fontSize: 14, ml: 0.5, color: 'text.disabled' }} />
            </Tooltip>
          )}
        </Typography>
        <Typography variant="body2" fontWeight="medium">
          {displayValue}{unit}
        </Typography>
      </Box>
      {showProgress && (
        <LinearProgress
          variant="determinate"
          value={Math.min((value / maxValue) * 100, 100)}
          color={color}
          sx={{ height: 6, borderRadius: 3 }}
        />
      )}
    </Box>
  );
};

export const SERMetricsPanel: React.FC<SERMetricsPanelProps> = ({
  originalMetrics,
  correctedMetrics,
  improvementMetrics,
  compact = false,
}) => {
  const [expanded, setExpanded] = React.useState(!compact);

  const getQualityColor = (qualityLevel: string): 'success' | 'warning' | 'error' => {
    switch (qualityLevel) {
      case 'high': return 'success';
      case 'medium': return 'warning';
      case 'low': return 'error';
      default: return 'warning';
    }
  };

  const getImprovementIcon = (improvement: number) => {
    if (improvement > 5) return <ImprovementIcon color="success" />;
    if (improvement < -5) return <DegradationIcon color="error" />;
    return <NoChangeIcon color="info" />;
  };

  const getImprovementColor = (improvement: number): 'success' | 'error' | 'info' => {
    if (improvement > 5) return 'success';
    if (improvement < -5) return 'error';
    return 'info';
  };

  return (
    <Paper elevation={1} sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      {/* Header */}
      <Box sx={{ p: 2, borderBottom: 1, borderColor: 'divider' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Typography variant="h6">SER Metrics</Typography>
          {compact && (
            <IconButton size="small" onClick={() => setExpanded(!expanded)}>
              {expanded ? <ExpandLessIcon /> : <ExpandMoreIcon />}
            </IconButton>
          )}
        </Box>
      </Box>

      <Collapse in={expanded}>
        <Box sx={{ p: 2, flex: 1, overflow: 'auto' }}>
          {/* Improvement Summary */}
          <Card variant="outlined" sx={{ mb: 2, backgroundColor: 'background.default' }}>
            <CardContent sx={{ py: 2 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 1 }}>
                <Typography variant="subtitle2" fontWeight="bold">
                  Overall Improvement
                </Typography>
                {getImprovementIcon(improvementMetrics.improvement)}
              </Box>

              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <Typography variant="h4" fontWeight="bold" color={`${getImprovementColor(improvementMetrics.improvement)}.main`}>
                  {improvementMetrics.improvement > 0 ? '+' : ''}{improvementMetrics.improvement.toFixed(1)}%
                </Typography>
                <Box>
                  <Typography variant="body2" color="text.secondary">
                    {improvementMetrics.improvement_percentage.toFixed(1)}% relative improvement
                  </Typography>
                  <Chip
                    label={improvementMetrics.is_significant_improvement ? 'Significant' : 'Minor'}
                    size="small"
                    color={improvementMetrics.is_significant_improvement ? 'success' : 'default'}
                    variant="outlined"
                  />
                </Box>
              </Box>
            </CardContent>
          </Card>

          {/* Detailed Metrics Comparison */}
          <Grid container spacing={2}>
            {/* Original Metrics */}
            <Grid item xs={12} md={6}>
              <Card variant="outlined">
                <CardContent>
                  <Typography variant="subtitle2" fontWeight="bold" gutterBottom>
                    Original ASR
                  </Typography>

                  <MetricDisplay
                    label="SER Score"
                    value={originalMetrics.ser_score}
                    unit="%"
                    color="error"
                    showProgress
                    tooltip="Sentence Error Rate - lower is better"
                  />

                  <MetricDisplay
                    label="Edit Distance"
                    value={originalMetrics.edit_distance}
                    tooltip="Total number of edits required"
                  />

                  <MetricDisplay
                    label="Insertions"
                    value={originalMetrics.insertions}
                    color="info"
                    tooltip="Number of words that need to be inserted"
                  />

                  <MetricDisplay
                    label="Deletions"
                    value={originalMetrics.deletions}
                    color="warning"
                    tooltip="Number of words that need to be deleted"
                  />

                  <MetricDisplay
                    label="Substitutions"
                    value={originalMetrics.substitutions}
                    color="secondary"
                    tooltip="Number of words that need to be replaced"
                  />

                  <MetricDisplay
                    label="Moves"
                    value={originalMetrics.moves}
                    color="primary"
                    tooltip="Number of words that need to be moved"
                  />

                  <Box sx={{ mt: 2 }}>
                    <Chip
                      label={`${originalMetrics.quality_level.toUpperCase()} Quality`}
                      color={getQualityColor(originalMetrics.quality_level)}
                      size="small"
                    />
                  </Box>
                </CardContent>
              </Card>
            </Grid>

            {/* Corrected Metrics */}
            <Grid item xs={12} md={6}>
              <Card variant="outlined">
                <CardContent>
                  <Typography variant="subtitle2" fontWeight="bold" gutterBottom>
                    RAG Corrected
                  </Typography>

                  <MetricDisplay
                    label="SER Score"
                    value={correctedMetrics.ser_score}
                    unit="%"
                    color={correctedMetrics.ser_score < originalMetrics.ser_score ? 'success' : 'error'}
                    showProgress
                    tooltip="Sentence Error Rate - lower is better"
                  />

                  <MetricDisplay
                    label="Edit Distance"
                    value={correctedMetrics.edit_distance}
                    tooltip="Total number of edits required"
                  />

                  <MetricDisplay
                    label="Insertions"
                    value={correctedMetrics.insertions}
                    color="info"
                    tooltip="Number of words that need to be inserted"
                  />

                  <MetricDisplay
                    label="Deletions"
                    value={correctedMetrics.deletions}
                    color="warning"
                    tooltip="Number of words that need to be deleted"
                  />

                  <MetricDisplay
                    label="Substitutions"
                    value={correctedMetrics.substitutions}
                    color="secondary"
                    tooltip="Number of words that need to be replaced"
                  />

                  <MetricDisplay
                    label="Moves"
                    value={correctedMetrics.moves}
                    color="primary"
                    tooltip="Number of words that need to be moved"
                  />

                  <Box sx={{ mt: 2 }}>
                    <Chip
                      label={`${correctedMetrics.quality_level.toUpperCase()} Quality`}
                      color={getQualityColor(correctedMetrics.quality_level)}
                      size="small"
                    />
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          </Grid>

          {/* Quality Assessment */}
          <Card variant="outlined" sx={{ mt: 2 }}>
            <CardContent>
              <Typography variant="subtitle2" fontWeight="bold" gutterBottom>
                Quality Assessment
              </Typography>

              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <Box sx={{ textAlign: 'center' }}>
                    <Typography variant="body2" color="text.secondary">
                      Original Quality
                    </Typography>
                    <Chip
                      label={originalMetrics.is_acceptable_quality ? 'Acceptable' : 'Needs Work'}
                      color={originalMetrics.is_acceptable_quality ? 'success' : 'error'}
                      variant="outlined"
                    />
                  </Box>
                </Grid>

                <Grid item xs={6}>
                  <Box sx={{ textAlign: 'center' }}>
                    <Typography variant="body2" color="text.secondary">
                      Corrected Quality
                    </Typography>
                    <Chip
                      label={correctedMetrics.is_acceptable_quality ? 'Acceptable' : 'Needs Work'}
                      color={correctedMetrics.is_acceptable_quality ? 'success' : 'error'}
                      variant="outlined"
                    />
                  </Box>
                </Grid>
              </Grid>
            </CardContent>
          </Card>

          {/* Improvement Details */}
          {!compact && (
            <Card variant="outlined" sx={{ mt: 2 }}>
              <CardContent>
                <Typography variant="subtitle2" fontWeight="bold" gutterBottom>
                  Improvement Analysis
                </Typography>

                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Typography variant="body2">Absolute Improvement:</Typography>
                    <Typography variant="body2" fontWeight="medium">
                      {improvementMetrics.improvement > 0 ? '+' : ''}{improvementMetrics.improvement.toFixed(2)}%
                    </Typography>
                  </Box>

                  <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Typography variant="body2">Relative Improvement:</Typography>
                    <Typography variant="body2" fontWeight="medium">
                      {improvementMetrics.improvement_percentage.toFixed(2)}%
                    </Typography>
                  </Box>

                  <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Typography variant="body2">Significance:</Typography>
                    <Chip
                      label={improvementMetrics.is_significant_improvement ? 'Significant' : 'Not Significant'}
                      size="small"
                      color={improvementMetrics.is_significant_improvement ? 'success' : 'default'}
                      variant="outlined"
                    />
                  </Box>
                </Box>
              </CardContent>
            </Card>
          )}
        </Box>
      </Collapse>
    </Paper>
  );
};

export default SERMetricsPanel;

/**
 * Session Setup Dialog Component
 * 
 * Dialog for creating new MT validation sessions with
 * speaker selection, test data configuration, and settings.
 */

import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  Chip,
  Alert,
  Autocomplete,
  Switch,
  FormControlLabel,
  Stepper,
  Step,
  StepLabel,
  IconButton,
} from '@mui/material';
import {
  Close as CloseIcon,
  Person as PersonIcon,
  Assessment as AssessmentIcon,
  Settings as SettingsIcon,
} from '@mui/icons-material';
import { StartValidationSessionRequest } from '@/domain/types/mt-validation';
import { Speaker } from '@/domain/types/speaker';

interface SessionSetupDialogProps {
  open: boolean;
  onClose: () => void;
  onCreateSession: (sessionData: StartValidationSessionRequest) => void;
}

interface SessionFormData {
  session_name: string;
  speaker_id: string;
  speaker_name: string;
  test_data_count: number;
  mt_user_id: string;
  priority: 'low' | 'medium' | 'high';
  auto_advance: boolean;
  include_ser_metrics: boolean;
  randomize_order: boolean;
}

const steps = ['Speaker Selection', 'Test Data', 'Settings'];

// Mock data - in real app, this would come from API
const mockSpeakers: Speaker[] = [
  {
    speaker_id: '1',
    speaker_identifier: 'SPEAKER_001',
    speaker_name: 'Dr. John Smith',
    current_bucket: 'HIGH_TOUCH' as any,
    note_count: 150,
    average_ser_score: 18.5,
    quality_trend: 'improving' as any,
    should_transition: true,
    has_sufficient_data: true,
    created_at: '2025-01-01',
    updated_at: '2025-01-01',
  },
  {
    speaker_id: '2',
    speaker_identifier: 'SPEAKER_002',
    speaker_name: 'Dr. Sarah Johnson',
    current_bucket: 'MEDIUM_TOUCH' as any,
    note_count: 89,
    average_ser_score: 12.3,
    quality_trend: 'stable' as any,
    should_transition: false,
    has_sufficient_data: true,
    created_at: '2025-01-01',
    updated_at: '2025-01-01',
  },
];

export const SessionSetupDialog: React.FC<SessionSetupDialogProps> = ({
  open,
  onClose,
  onCreateSession,
}) => {
  const [activeStep, setActiveStep] = useState(0);
  const [formData, setFormData] = useState<SessionFormData>({
    session_name: '',
    speaker_id: '',
    speaker_name: '',
    test_data_count: 20,
    mt_user_id: 'current-user', // Would come from auth context
    priority: 'medium',
    auto_advance: true,
    include_ser_metrics: true,
    randomize_order: false,
  });
  const [selectedSpeaker, setSelectedSpeaker] = useState<Speaker | null>(null);
  const [errors, setErrors] = useState<Record<string, string>>({});

  // Reset form when dialog opens
  useEffect(() => {
    if (open) {
      setActiveStep(0);
      setFormData({
        session_name: '',
        speaker_id: '',
        speaker_name: '',
        test_data_count: 20,
        mt_user_id: 'current-user',
        priority: 'medium',
        auto_advance: true,
        include_ser_metrics: true,
        randomize_order: false,
      });
      setSelectedSpeaker(null);
      setErrors({});
    }
  }, [open]);

  // Validation
  const validateStep = (step: number): boolean => {
    const newErrors: Record<string, string> = {};

    switch (step) {
      case 0: // Speaker Selection
        if (!formData.speaker_id) {
          newErrors.speaker_id = 'Please select a speaker';
        }
        if (!formData.session_name.trim()) {
          newErrors.session_name = 'Session name is required';
        }
        break;
      case 1: // Test Data
        if (formData.test_data_count < 1 || formData.test_data_count > 100) {
          newErrors.test_data_count = 'Test data count must be between 1 and 100';
        }
        break;
      case 2: // Settings
        // No validation needed for settings step
        break;
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle field changes
  const handleFieldChange = (field: keyof SessionFormData, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));

    // Clear error for this field
    if (errors[field]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[field];
        return newErrors;
      });
    }
  };

  // Handle speaker selection
  const handleSpeakerSelect = (speaker: Speaker | null) => {
    setSelectedSpeaker(speaker);
    if (speaker) {
      handleFieldChange('speaker_id', speaker.speaker_id);
      handleFieldChange('speaker_name', speaker.speaker_name);

      // Auto-generate session name
      if (!formData.session_name) {
        const timestamp = new Date().toLocaleDateString();
        handleFieldChange('session_name', `Validation - ${speaker.speaker_name} - ${timestamp}`);
      }
    } else {
      handleFieldChange('speaker_id', '');
      handleFieldChange('speaker_name', '');
    }
  };

  // Navigation
  const handleNext = () => {
    if (validateStep(activeStep)) {
      setActiveStep(prev => prev + 1);
    }
  };

  const handleBack = () => {
    setActiveStep(prev => prev - 1);
  };

  // Handle form submission
  const handleSubmit = () => {
    if (!validateStep(activeStep)) return;

    const sessionData: StartValidationSessionRequest = {
      speaker_id: formData.speaker_id,
      session_name: formData.session_name,
      test_data_ids: [], // Would be populated based on speaker and criteria
      mt_user_id: formData.mt_user_id,
      session_metadata: {
        priority: formData.priority,
        auto_advance: formData.auto_advance,
        include_ser_metrics: formData.include_ser_metrics,
        randomize_order: formData.randomize_order,
        test_data_count: formData.test_data_count,
      },
    };

    onCreateSession(sessionData);
  };

  const renderStepContent = (step: number) => {
    switch (step) {
      case 0:
        return (
          <Box>
            <Typography variant="h6" gutterBottom>
              Select Speaker and Session Details
            </Typography>

            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Session Name"
                  value={formData.session_name}
                  onChange={(e) => handleFieldChange('session_name', e.target.value)}
                  error={!!errors.session_name}
                  helperText={errors.session_name}
                  placeholder="e.g., Quality Review - Dr. Smith - Jan 2025"
                />
              </Grid>

              <Grid item xs={12}>
                <Autocomplete
                  options={mockSpeakers}
                  getOptionLabel={(option) => `${option.speaker_name} (${option.speaker_identifier})`}
                  value={selectedSpeaker}
                  onChange={(_, value) => handleSpeakerSelect(value)}
                  renderInput={(params) => (
                    <TextField
                      {...params}
                      label="Select Speaker"
                      error={!!errors.speaker_id}
                      helperText={errors.speaker_id || 'Choose the speaker to validate'}
                    />
                  )}
                  renderOption={(props, option) => (
                    <Box component="li" {...props}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, width: '100%' }}>
                        <PersonIcon color="primary" />
                        <Box sx={{ flex: 1 }}>
                          <Typography variant="body1">{option.speaker_name}</Typography>
                          <Typography variant="caption" color="text.secondary">
                            {option.speaker_identifier}  {option.note_count} notes  
                            SER: {option.average_ser_score.toFixed(1)}%
                          </Typography>
                        </Box>
                        <Chip
                          label={option.current_bucket.replace('_', ' ')}
                          size="small"
                          color={option.should_transition ? 'warning' : 'default'}
                        />
                      </Box>
                    </Box>
                  )}
                />
              </Grid>

              {selectedSpeaker && (
                <Grid item xs={12}>
                  <Card variant="outlined">
                    <CardContent>
                      <Typography variant="subtitle2" gutterBottom>
                        Speaker Summary
                      </Typography>
                      <Grid container spacing={2}>
                        <Grid item xs={6}>
                          <Typography variant="body2" color="text.secondary">
                            Current Bucket
                          </Typography>
                          <Typography variant="body1">
                            {selectedSpeaker.current_bucket.replace('_', ' ')}
                          </Typography>
                        </Grid>
                        <Grid item xs={6}>
                          <Typography variant="body2" color="text.secondary">
                            Average SER Score
                          </Typography>
                          <Typography variant="body1">
                            {selectedSpeaker.average_ser_score.toFixed(1)}%
                          </Typography>
                        </Grid>
                        <Grid item xs={6}>
                          <Typography variant="body2" color="text.secondary">
                            Total Notes
                          </Typography>
                          <Typography variant="body1">
                            {selectedSpeaker.note_count}
                          </Typography>
                        </Grid>
                        <Grid item xs={6}>
                          <Typography variant="body2" color="text.secondary">
                            Quality Trend
                          </Typography>
                          <Typography variant="body1" sx={{ textTransform: 'capitalize' }}>
                            {selectedSpeaker.quality_trend.replace('_', ' ')}
                          </Typography>
                        </Grid>
                      </Grid>

                      {selectedSpeaker.should_transition && (
                        <Alert severity="info" sx={{ mt: 2 }}>
                          This speaker is recommended for bucket transition based on quality improvements.
                        </Alert>
                      )}
                    </CardContent>
                  </Card>
                </Grid>
              )}
            </Grid>
          </Box>
        );

      case 1:
        return (
          <Box>
            <Typography variant="h6" gutterBottom>
              Configure Test Data
            </Typography>

            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  type="number"
                  label="Number of Test Items"
                  value={formData.test_data_count}
                  onChange={(e) => handleFieldChange('test_data_count', parseInt(e.target.value))}
                  error={!!errors.test_data_count}
                  helperText={errors.test_data_count || 'Recommended: 15-25 items'}
                  inputProps={{ min: 1, max: 100 }}
                />
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <InputLabel>Priority</InputLabel>
                  <Select
                    value={formData.priority}
                    onChange={(e) => handleFieldChange('priority', e.target.value)}
                    label="Priority"
                  >
                    <MenuItem value="low">Low</MenuItem>
                    <MenuItem value="medium">Medium</MenuItem>
                    <MenuItem value="high">High</MenuItem>
                  </Select>
                </FormControl>
              </Grid>

              <Grid item xs={12}>
                <FormControlLabel
                  control={
                    <Switch
                      checked={formData.randomize_order}
                      onChange={(e) => handleFieldChange('randomize_order', e.target.checked)}
                    />
                  }
                  label="Randomize item order"
                />
                <Typography variant="caption" color="text.secondary" display="block">
                  Randomizing helps reduce bias in validation results
                </Typography>
              </Grid>
            </Grid>
          </Box>
        );

      case 2:
        return (
          <Box>
            <Typography variant="h6" gutterBottom>
              Validation Settings
            </Typography>

            <Grid container spacing={2}>
              <Grid item xs={12}>
                <FormControlLabel
                  control={
                    <Switch
                      checked={formData.auto_advance}
                      onChange={(e) => handleFieldChange('auto_advance', e.target.checked)}
                    />
                  }
                  label="Auto-advance to next item after feedback submission"
                />
              </Grid>

              <Grid item xs={12}>
                <FormControlLabel
                  control={
                    <Switch
                      checked={formData.include_ser_metrics}
                      onChange={(e) => handleFieldChange('include_ser_metrics', e.target.checked)}
                    />
                  }
                  label="Show SER metrics panel by default"
                />
              </Grid>
            </Grid>

            <Alert severity="info" sx={{ mt: 2 }}>
              These settings can be changed during the validation session.
            </Alert>
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <Dialog
      open={open}
      onClose={onClose}
      maxWidth="md"
      fullWidth
      PaperProps={{ sx: { height: '80vh' } }}
    >
      <DialogTitle>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Typography variant="h6">Create Validation Session</Typography>
          <IconButton onClick={onClose} size="small">
            <CloseIcon />
          </IconButton>
        </Box>
      </DialogTitle>

      <DialogContent>
        <Box sx={{ mb: 3 }}>
          <Stepper activeStep={activeStep}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>
        </Box>

        {renderStepContent(activeStep)}
      </DialogContent>

      <DialogActions>
        <Button onClick={onClose}>
          Cancel
        </Button>

        {activeStep > 0 && (
          <Button onClick={handleBack}>
            Back
          </Button>
        )}

        {activeStep < steps.length - 1 ? (
          <Button
            onClick={handleNext}
            variant="contained"
            disabled={!formData.speaker_id && activeStep === 0}
          >
            Next
          </Button>
        ) : (
          <Button
            onClick={handleSubmit}
            variant="contained"
            color="primary"
          >
            Create Session
          </Button>
        )}
      </DialogActions>
    </Dialog>
  );
};

