/**
 * MT Validation Page
 * 
 * Main page for medical transcriptionist validation workflow
 * with session management, progress tracking, and comprehensive interface.
 */

import React, { useEffect, useState, useCallback } from 'react';
import {
  Box,
  Container,
  Typography,
  Paper,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
  Snackbar,
  Fab,
  SpeedDial,
  SpeedDialAction,
  SpeedDialIcon,
  Card,
  CardContent,
  Grid,
  Chip,
  LinearProgress,
} from '@mui/material';
import {
  PlayArrow as StartIcon,
  Pause as PauseIcon,
  Stop as StopIcon,
  Settings as SettingsIcon,
  Assessment as AssessmentIcon,
  History as HistoryIcon,
  Download as ExportIcon,
  Add as AddIcon,
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { useAppDispatch, useAppSelector } from '@/app/hooks';
import {
  startValidationSession,
  getValidationSession,
  getValidationTestData,
  completeValidationSession,
  resetValidationState,
  selectCurrentSession,
  selectTestDataItems,
  selectSessionProgress,
  selectValidationLoading,
  selectValidationError,
  selectWorkflowState,
} from '../mt-validation-slice';
import {
  ValidationSession,
  StartValidationSessionRequest,
  SessionStatus,
} from '@/domain/types/mt-validation';

// Components
import MTValidationInterface from '../components/MTValidationInterface';
import ValidationProgress from '../components/ValidationProgress';
import SessionSetupDialog from '../components/SessionSetupDialog';
import SessionSummaryDialog from '../components/SessionSummaryDialog';

export const MTValidationPage: React.FC = () => {
  const { sessionId } = useParams<{ sessionId?: string }>();
  const navigate = useNavigate();
  const dispatch = useAppDispatch();

  // Redux state
  const currentSession = useAppSelector(selectCurrentSession);
  const testDataItems = useAppSelector(selectTestDataItems);
  const sessionProgress = useAppSelector(selectSessionProgress);
  const loading = useAppSelector(selectValidationLoading);
  const error = useAppSelector(selectValidationError);
  const workflowState = useAppSelector(selectWorkflowState);

  // Local state
  const [showSetupDialog, setShowSetupDialog] = useState(false);
  const [showSummaryDialog, setShowSummaryDialog] = useState(false);
  const [speedDialOpen, setSpeedDialOpen] = useState(false);
  const [notification, setNotification] = useState<{
    open: boolean;
    message: string;
    severity: 'success' | 'error' | 'warning' | 'info';
  }>({
    open: false,
    message: '',
    severity: 'info',
  });

  // Initialize session
  useEffect(() => {
    if (sessionId) {
      // Load existing session
      dispatch(getValidationSession(sessionId));
      dispatch(getValidationTestData({ sessionId }));
    } else {
      // Show setup dialog for new session
      setShowSetupDialog(true);
    }

    // Cleanup on unmount
    return () => {
      dispatch(resetValidationState());
    };
  }, [dispatch, sessionId]);

  // Handle session creation
  const handleCreateSession = useCallback(async (sessionData: StartValidationSessionRequest) => {
    try {
      const session = await dispatch(startValidationSession(sessionData)).unwrap();
      await dispatch(getValidationTestData({ sessionId: session.session_id }));

      setShowSetupDialog(false);
      setNotification({
        open: true,
        message: `Validation session "${session.session_name}" started successfully`,
        severity: 'success',
      });

      // Navigate to the session URL
      navigate(`/mt-validation/${session.session_id}`);
    } catch (error: any) {
      setNotification({
        open: true,
        message: error.message || 'Failed to create validation session',
        severity: 'error',
      });
    }
  }, [dispatch, navigate]);

  // Handle session completion
  const handleCompleteSession = useCallback(async () => {
    if (!currentSession) return;

    try {
      await dispatch(completeValidationSession({
        sessionId: currentSession.session_id,
        request: {
          session_id: currentSession.session_id,
          completion_notes: 'Session completed successfully',
        },
      })).unwrap();

      setShowSummaryDialog(true);
      setNotification({
        open: true,
        message: 'Validation session completed successfully',
        severity: 'success',
      });
    } catch (error: any) {
      setNotification({
        open: true,
        message: error.message || 'Failed to complete session',
        severity: 'error',
      });
    }
  }, [dispatch, currentSession]);

  // Handle session pause
  const handlePauseSession = useCallback(() => {
    setNotification({
      open: true,
      message: 'Session paused. You can resume later.',
      severity: 'info',
    });
    navigate('/mt-validation');
  }, [navigate]);

  // Speed dial actions
  const speedDialActions = [
    {
      icon: <SettingsIcon />,
      name: 'Settings',
      onClick: () => {
        // Open settings dialog
      },
    },
    {
      icon: <AssessmentIcon />,
      name: 'Session Analytics',
      onClick: () => {
        // Open analytics dialog
      },
    },
    {
      icon: <HistoryIcon />,
      name: 'Session History',
      onClick: () => {
        navigate('/mt-validation/history');
      },
    },
    {
      icon: <ExportIcon />,
      name: 'Export Data',
      onClick: () => {
        // Export session data
      },
    },
  ];

  // Handle notification close
  const handleNotificationClose = () => {
    setNotification({ ...notification, open: false });
  };

  // Render session setup
  if (!sessionId && !currentSession) {
    return (
      <Container maxWidth="lg" sx={{ py: 3 }}>
        <Box sx={{ textAlign: 'center', py: 8 }}>
          <Typography variant="h4" gutterBottom>
            MT Validation Workflow
          </Typography>
          <Typography variant="body1" color="text.secondary" sx={{ mb: 4 }}>
            Start a new validation session to review RAG-corrected ASR drafts
          </Typography>

          <Button
            variant="contained"
            size="large"
            startIcon={<StartIcon />}
            onClick={() => setShowSetupDialog(true)}
          >
            Start New Session
          </Button>
        </Box>

        {/* Session Setup Dialog */}
        <SessionSetupDialog
          open={showSetupDialog}
          onClose={() => setShowSetupDialog(false)}
          onCreateSession={handleCreateSession}
        />
      </Container>
    );
  }

  // Render loading state
  if (loading.session || loading.testData) {
    return (
      <Box sx={{ 
        display: 'flex', 
        flexDirection: 'column',
        alignItems: 'center', 
        justifyContent: 'center', 
        height: '100vh',
        gap: 2
      }}>
        <LinearProgress sx={{ width: 300 }} />
        <Typography variant="body1" color="text.secondary">
          {loading.session ? 'Loading session...' : 'Loading validation data...'}
        </Typography>
      </Box>
    );
  }

  // Render error state
  if (error.session || error.testData) {
    return (
      <Container maxWidth="lg" sx={{ py: 3 }}>
        <Alert severity="error" sx={{ mb: 3 }}>
          {error.session || error.testData}
        </Alert>
        <Button
          variant="outlined"
          onClick={() => navigate('/mt-validation')}
        >
          Back to Sessions
        </Button>
      </Container>
    );
  }

  // Render session completed state
  if (currentSession?.status === SessionStatus.COMPLETED) {
    return (
      <Container maxWidth="lg" sx={{ py: 3 }}>
        <Card>
          <CardContent sx={{ textAlign: 'center', py: 6 }}>
            <Typography variant="h4" gutterBottom color="success.main">
              Session Completed!
            </Typography>
            <Typography variant="body1" color="text.secondary" sx={{ mb: 4 }}>
              Validation session "{currentSession.session_name}" has been completed successfully.
            </Typography>

            <Grid container spacing={2} sx={{ mb: 4 }}>
              <Grid item xs={12} sm={4}>
                <Typography variant="h6" fontWeight="bold">
                  {testDataItems.length}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Items Reviewed
                </Typography>
              </Grid>
              <Grid item xs={12} sm={4}>
                <Typography variant="h6" fontWeight="bold">
                  {currentSession.duration_minutes || 0}min
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Total Duration
                </Typography>
              </Grid>
              <Grid item xs={12} sm={4}>
                <Typography variant="h6" fontWeight="bold">
                  100%
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Completion Rate
                </Typography>
              </Grid>
            </Grid>

            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
              <Button
                variant="outlined"
                onClick={() => setShowSummaryDialog(true)}
              >
                View Summary
              </Button>
              <Button
                variant="contained"
                onClick={() => navigate('/mt-validation')}
              >
                Back to Sessions
              </Button>
            </Box>
          </CardContent>
        </Card>
      </Container>
    );
  }

  // Render main validation interface
  return (
    <Box sx={{ height: '100vh', overflow: 'hidden' }}>
      {/* Main Validation Interface */}
      {currentSession && testDataItems.length > 0 && (
        <MTValidationInterface
          sessionId={currentSession.session_id}
          onComplete={handleCompleteSession}
          onPause={handlePauseSession}
        />
      )}

      {/* Speed Dial */}
      <SpeedDial
        ariaLabel="Validation actions"
        sx={{ position: 'fixed', bottom: 16, left: 16 }}
        icon={<SpeedDialIcon />}
        onClose={() => setSpeedDialOpen(false)}
        onOpen={() => setSpeedDialOpen(true)}
        open={speedDialOpen}
        direction="up"
      >
        {speedDialActions.map((action) => (
          <SpeedDialAction
            key={action.name}
            icon={action.icon}
            tooltipTitle={action.name}
            onClick={() => {
              action.onClick();
              setSpeedDialOpen(false);
            }}
          />
        ))}
      </SpeedDial>

      {/* Session Summary Dialog */}
      {showSummaryDialog && currentSession && (
        <SessionSummaryDialog
          session={currentSession}
          open={showSummaryDialog}
          onClose={() => setShowSummaryDialog(false)}
        />
      )}

      {/* Notification Snackbar */}
      <Snackbar
        open={notification.open}
        autoHideDuration={6000}
        onClose={handleNotificationClose}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert
          onClose={handleNotificationClose}
          severity={notification.severity}
          variant="filled"
          sx={{ width: '100%' }}
        >
          {notification.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default MTValidationPage;

/**
 * Create/Edit Speaker Dialog Component
 * 
 * Dialog for creating new speakers or editing existing ones
 * with form validation and error handling.
 */

import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Box,
  Typography,
  Alert,
  Grid,
  IconButton,
} from '@mui/material';
import {
  Close as CloseIcon,
  Save as SaveIcon,
  Person as PersonIcon,
} from '@mui/icons-material';
import { useAppDispatch } from '@/app/hooks';
import { createSpeaker, updateSpeaker } from '../speaker-slice';
import { Speaker, SpeakerBucket, SpeakerFormData } from '@/domain/types/speaker';

interface CreateSpeakerDialogProps {
  speaker?: Speaker | null; // null for create, speaker object for edit
  open: boolean;
  onClose: () => void;
  onSpeakerCreated: (speaker: Speaker) => void;
}

interface FormErrors {
  speaker_identifier?: string;
  speaker_name?: string;
  initial_bucket?: string;
}

export const CreateSpeakerDialog: React.FC<CreateSpeakerDialogProps> = ({
  speaker,
  open,
  onClose,
  onSpeakerCreated,
}) => {
  const dispatch = useAppDispatch();

  // Form state
  const [formData, setFormData] = useState<SpeakerFormData>({
    speaker_identifier: '',
    speaker_name: '',
    initial_bucket: SpeakerBucket.HIGH_TOUCH,
    metadata: {},
  });

  const [errors, setErrors] = useState<FormErrors>({});
  const [loading, setLoading] = useState(false);
  const [submitError, setSubmitError] = useState<string | null>(null);

  // Initialize form data when speaker changes
  useEffect(() => {
    if (speaker) {
      // Edit mode
      setFormData({
        speaker_identifier: speaker.speaker_identifier,
        speaker_name: speaker.speaker_name,
        initial_bucket: speaker.current_bucket,
        metadata: speaker.metadata || {},
      });
    } else {
      // Create mode
      setFormData({
        speaker_identifier: '',
        speaker_name: '',
        initial_bucket: SpeakerBucket.HIGH_TOUCH,
        metadata: {},
      });
    }

    // Clear errors when dialog opens/closes
    setErrors({});
    setSubmitError(null);
  }, [speaker, open]);

  // Form validation
  const validateForm = (): boolean => {
    const newErrors: FormErrors = {};

    if (!formData.speaker_identifier.trim()) {
      newErrors.speaker_identifier = 'Speaker identifier is required';
    } else if (!/^[A-Z0-9_]+$/.test(formData.speaker_identifier)) {
      newErrors.speaker_identifier = 'Speaker identifier must contain only uppercase letters, numbers, and underscores';
    }

    if (!formData.speaker_name.trim()) {
      newErrors.speaker_name = 'Speaker name is required';
    } else if (formData.speaker_name.trim().length < 2) {
      newErrors.speaker_name = 'Speaker name must be at least 2 characters long';
    }

    if (!formData.initial_bucket) {
      newErrors.initial_bucket = 'Initial bucket is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form field changes
  const handleFieldChange = (field: keyof SpeakerFormData, value: any) => {
    setFormData(prev => ({
      ...prev,
      [field]: value,
    }));

    // Clear field error when user starts typing
    if (errors[field as keyof FormErrors]) {
      setErrors(prev => ({
        ...prev,
        [field]: undefined,
      }));
    }
  };

  // Handle form submission
  const handleSubmit = async () => {
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    setSubmitError(null);

    try {
      let result;

      if (speaker) {
        // Edit mode
        result = await dispatch(updateSpeaker({
          speakerId: speaker.speaker_id,
          speakerData: formData,
        })).unwrap();
      } else {
        // Create mode
        result = await dispatch(createSpeaker(formData)).unwrap();
      }

      onSpeakerCreated(result);
    } catch (error: any) {
      setSubmitError(error.message || 'Failed to save speaker');
    } finally {
      setLoading(false);
    }
  };

  // Handle dialog close
  const handleClose = () => {
    if (!loading) {
      onClose();
    }
  };

  const isEditMode = !!speaker;
  const dialogTitle = isEditMode ? 'Edit Speaker' : 'Create New Speaker';

  return (
    <Dialog
      open={open}
      onClose={handleClose}
      maxWidth="sm"
      fullWidth
      PaperProps={{
        component: 'form',
        onSubmit: (e: React.FormEvent) => {
          e.preventDefault();
          handleSubmit();
        },
      }}
    >
      {/* Dialog Header */}
      <DialogTitle>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <PersonIcon color="primary" />
            <Typography variant="h6">{dialogTitle}</Typography>
          </Box>

          <IconButton onClick={handleClose} size="small" disabled={loading}>
            <CloseIcon />
          </IconButton>
        </Box>
      </DialogTitle>

      {/* Dialog Content */}
      <DialogContent>
        {submitError && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {submitError}
          </Alert>
        )}

        <Grid container spacing={2}>
          {/* Speaker Identifier */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Speaker Identifier"
              value={formData.speaker_identifier}
              onChange={(e) => handleFieldChange('speaker_identifier', e.target.value.toUpperCase())}
              error={!!errors.speaker_identifier}
              helperText={errors.speaker_identifier || 'Unique identifier (e.g., SPEAKER_001)'}
              required
              disabled={loading || isEditMode} // Don't allow editing identifier
              placeholder="SPEAKER_001"
            />
          </Grid>

          {/* Speaker Name */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Speaker Name"
              value={formData.speaker_name}
              onChange={(e) => handleFieldChange('speaker_name', e.target.value)}
              error={!!errors.speaker_name}
              helperText={errors.speaker_name || 'Full name of the speaker'}
              required
              disabled={loading}
              placeholder="Dr. John Smith"
            />
          </Grid>

          {/* Initial/Current Bucket */}
          <Grid item xs={12}>
            <FormControl fullWidth error={!!errors.initial_bucket} disabled={loading}>
              <InputLabel required>
                {isEditMode ? 'Current Bucket' : 'Initial Bucket'}
              </InputLabel>
              <Select
                value={formData.initial_bucket}
                onChange={(e) => handleFieldChange('initial_bucket', e.target.value)}
                label={isEditMode ? 'Current Bucket' : 'Initial Bucket'}
              >
                {Object.values(SpeakerBucket).map((bucket) => (
                  <MenuItem key={bucket} value={bucket}>
                    {bucket.replace('_', ' ')}
                  </MenuItem>
                ))}
              </Select>
              {errors.initial_bucket && (
                <Typography variant="caption" color="error" sx={{ mt: 0.5, ml: 1.5 }}>
                  {errors.initial_bucket}
                </Typography>
              )}
            </FormControl>
          </Grid>

          {/* Additional Information */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Department (Optional)"
              value={formData.metadata?.department || ''}
              onChange={(e) => handleFieldChange('metadata', {
                ...formData.metadata,
                department: e.target.value,
              })}
              disabled={loading}
              placeholder="Cardiology"
            />
          </Grid>

          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Location (Optional)"
              value={formData.metadata?.location || ''}
              onChange={(e) => handleFieldChange('metadata', {
                ...formData.metadata,
                location: e.target.value,
              })}
              disabled={loading}
              placeholder="Main Hospital"
            />
          </Grid>

          {/* Notes */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              multiline
              rows={3}
              label="Notes (Optional)"
              value={formData.metadata?.notes || ''}
              onChange={(e) => handleFieldChange('metadata', {
                ...formData.metadata,
                notes: e.target.value,
              })}
              disabled={loading}
              placeholder="Additional notes about this speaker..."
            />
          </Grid>
        </Grid>

        {/* Information Box */}
        <Box sx={{ mt: 2 }}>
          <Alert severity="info">
            <Typography variant="body2">
              {isEditMode 
                ? 'Changes will be saved immediately. The speaker identifier cannot be modified.'
                : 'New speakers will be created with the specified initial bucket. Quality metrics will be calculated as data becomes available.'
              }
            </Typography>
          </Alert>
        </Box>
      </DialogContent>

      {/* Dialog Actions */}
      <DialogActions sx={{ px: 3, py: 2 }}>
        <Button 
          onClick={handleClose} 
          disabled={loading}
        >
          Cancel
        </Button>

        <Button
          type="submit"
          variant="contained"
          startIcon={<SaveIcon />}
          disabled={loading}
          sx={{ minWidth: 120 }}
        >
          {loading ? 'Saving...' : (isEditMode ? 'Update' : 'Create')}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

