                    </Box>
                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body2" color="text.secondary">
                        Data Sufficiency
                      </Typography>
                      <Chip
                        label={speaker.has_sufficient_data ? 'Sufficient' : 'Limited'}
                        color={speaker.has_sufficient_data ? 'success' : 'warning'}
                        size="small"
                        variant="outlined"
