/**
 * Speaker Search and Selection Component
 * 
 * Main component for searching, filtering, and selecting speakers
 * with comprehensive filtering options and real-time search.
 */

import React, { useEffect, useState, useCallback } from 'react';
import {
  Box,
  Paper,
  TextField,
  InputAdornment,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  Button,
  IconButton,
  Tooltip,
  Typography,
  Divider,
  Collapse,
  Grid,
  Alert,
} from '@mui/material';
import {
  Search as SearchIcon,
  FilterList as FilterIcon,
  Clear as ClearIcon,
  ExpandMore as ExpandMoreIcon,
  ExpandLess as ExpandLessIcon,
  Refresh as RefreshIcon,
  ViewList as ViewListIcon,
  ViewModule as ViewModuleIcon,
  Analytics as AnalyticsIcon,
} from '@mui/icons-material';
import { useAppDispatch, useAppSelector } from '@/app/hooks';
import {
  searchSpeakers,
  setSearchFilters,
  clearSearchFilters,
  setCurrentPage,
  setViewMode,
  getQuickFilterOptions,
  selectSpeakers,
  selectSpeakersLoading,
  selectSpeakersError,
  selectSearchFilters,
  selectPagination,
  selectViewMode,
  selectQuickFilters,
} from '../speaker-slice';
import { SpeakerBucket, QualityTrend, SpeakerSearchFilters } from '@/domain/types/speaker';
// Note: lodash debounce would need to be installed or implemented
// For now, using a simple debounce implementation
const debounce = (func: Function, wait: number) => {
  let timeout: NodeJS.Timeout;
  return function executedFunction(...args: any[]) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
};

interface SpeakerSearchAndSelectionProps {
  onSpeakerSelect?: (speakerId: string) => void;
  multiSelect?: boolean;
  showViewModeToggle?: boolean;
  showQuickFilters?: boolean;
  compact?: boolean;
}

export const SpeakerSearchAndSelection: React.FC<SpeakerSearchAndSelectionProps> = ({
  onSpeakerSelect,
  multiSelect = false,
  showViewModeToggle = true,
  showQuickFilters = true,
  compact = false,
}) => {
  const dispatch = useAppDispatch();

  // Redux state
  const speakers = useAppSelector(selectSpeakers);
  const loading = useAppSelector(selectSpeakersLoading);
  const error = useAppSelector(selectSpeakersError);
  const searchFilters = useAppSelector(selectSearchFilters);
  const pagination = useAppSelector(selectPagination);
  const viewMode = useAppSelector(selectViewMode);
  const quickFilters = useAppSelector(selectQuickFilters);

  // Local state
  const [searchText, setSearchText] = useState(searchFilters.name_pattern || '');
  const [showAdvancedFilters, setShowAdvancedFilters] = useState(false);
  const [localFilters, setLocalFilters] = useState<SpeakerSearchFilters>(searchFilters);

  // Debounced search function
  const debouncedSearch = useCallback(
    debounce((filters: SpeakerSearchFilters) => {
      dispatch(setSearchFilters(filters));
      dispatch(searchSpeakers({ ...filters, page: 1, page_size: pagination.pageSize }));
    }, 300),
    [dispatch, pagination.pageSize]
  );

  // Initialize data
  useEffect(() => {
    if (quickFilters.length === 0) {
      dispatch(getQuickFilterOptions());
    }

    // Initial search if no speakers loaded
    if (speakers.length === 0 && !loading) {
      dispatch(searchSpeakers({ page: 1, page_size: pagination.pageSize }));
    }
  }, [dispatch, quickFilters.length, speakers.length, loading, pagination.pageSize]);

  // Handle search text change
  const handleSearchTextChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value;
    setSearchText(value);

    const newFilters = { ...localFilters, name_pattern: value || undefined };
    setLocalFilters(newFilters);
    debouncedSearch(newFilters);
  };

  // Handle filter changes
  const handleFilterChange = (field: keyof SpeakerSearchFilters, value: any) => {
    const newFilters = { ...localFilters, [field]: value || undefined };
    setLocalFilters(newFilters);
    debouncedSearch(newFilters);
  };

  // Handle quick filter selection
  const handleQuickFilterSelect = (filterOption: any) => {
    const newFilters = { ...filterOption.filters };
    setLocalFilters(newFilters);
    setSearchText(newFilters.name_pattern || '');
    dispatch(setSearchFilters(newFilters));
    dispatch(searchSpeakers({ ...newFilters, page: 1, page_size: pagination.pageSize }));
  };

  // Clear all filters
  const handleClearFilters = () => {
    setSearchText('');
    setLocalFilters({});
    dispatch(clearSearchFilters());
    dispatch(searchSpeakers({ page: 1, page_size: pagination.pageSize }));
  };

  // Refresh data
  const handleRefresh = () => {
    dispatch(searchSpeakers({ ...searchFilters, page: pagination.currentPage, page_size: pagination.pageSize }));
    dispatch(getQuickFilterOptions());
  };

  // Get active filter count
  const getActiveFilterCount = () => {
    return Object.values(localFilters).filter(value => 
      value !== undefined && value !== null && value !== ''
    ).length;
  };

  return (
    <Paper elevation={1} sx={{ p: compact ? 2 : 3 }}>
      {/* Header */}
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
        <Typography variant={compact ? 'h6' : 'h5'} component="h2">
          Speaker Search & Selection
        </Typography>

        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          {showViewModeToggle && (
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <Tooltip title="Table View">
                <IconButton
                  size="small"
                  onClick={() => dispatch(setViewMode('table'))}
                  color={viewMode === 'table' ? 'primary' : 'default'}
                >
                  <ViewListIcon />
                </IconButton>
              </Tooltip>
              <Tooltip title="Grid View">
                <IconButton
                  size="small"
                  onClick={() => dispatch(setViewMode('grid'))}
                  color={viewMode === 'grid' ? 'primary' : 'default'}
                >
                  <ViewModuleIcon />
                </IconButton>
              </Tooltip>
              <Tooltip title="Analytics View">
                <IconButton
                  size="small"
                  onClick={() => dispatch(setViewMode('analytics'))}
                  color={viewMode === 'analytics' ? 'primary' : 'default'}
                >
                  <AnalyticsIcon />
                </IconButton>
              </Tooltip>
            </Box>
          )}

          <Tooltip title="Refresh">
            <IconButton size="small" onClick={handleRefresh} disabled={loading}>
              <RefreshIcon />
            </IconButton>
          </Tooltip>
        </Box>
      </Box>

      {/* Error Alert */}
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {/* Quick Filters */}
      {showQuickFilters && quickFilters.length > 0 && (
        <Box sx={{ mb: 2 }}>
          <Typography variant="subtitle2" sx={{ mb: 1 }}>
            Quick Filters
          </Typography>
          <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
            {quickFilters.map((filter) => (
              <Chip
                key={filter.value}
                label={`${filter.label} (${filter.count})`}
                onClick={() => handleQuickFilterSelect(filter)}
                variant="outlined"
                size="small"
                clickable
              />
            ))}
          </Box>
        </Box>
      )}

      {/* Search Bar */}
      <Box sx={{ mb: 2 }}>
        <TextField
          fullWidth
          placeholder="Search speakers by name or identifier..."
          value={searchText}
          onChange={handleSearchTextChange}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon />
              </InputAdornment>
            ),
            endAdornment: searchText && (
              <InputAdornment position="end">
                <IconButton size="small" onClick={() => handleSearchTextChange({ target: { value: '' } } as any)}>
                  <ClearIcon />
                </IconButton>
              </InputAdornment>
            ),
          }}
          disabled={loading}
        />
      </Box>

      {/* Filter Controls */}
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
        <Button
          startIcon={<FilterIcon />}
          endIcon={showAdvancedFilters ? <ExpandLessIcon /> : <ExpandMoreIcon />}
          onClick={() => setShowAdvancedFilters(!showAdvancedFilters)}
          variant="outlined"
          size="small"
        >
          Advanced Filters
          {getActiveFilterCount() > 0 && (
            <Chip
              label={getActiveFilterCount()}
              size="small"
              color="primary"
              sx={{ ml: 1, height: 20, fontSize: '0.75rem' }}
            />
          )}
        </Button>

        {getActiveFilterCount() > 0 && (
          <Button
            startIcon={<ClearIcon />}
            onClick={handleClearFilters}
            variant="text"
            size="small"
            color="secondary"
          >
            Clear Filters
          </Button>
        )}
      </Box>

      {/* Advanced Filters */}
      <Collapse in={showAdvancedFilters}>
        <Paper variant="outlined" sx={{ p: 2, mb: 2 }}>
          <Grid container spacing={2}>
            {/* Bucket Filter */}
            <Grid item xs={12} sm={6} md={3}>
              <FormControl fullWidth size="small">
                <InputLabel>Bucket</InputLabel>
                <Select
                  value={localFilters.bucket || ''}
                  onChange={(e) => handleFilterChange('bucket', e.target.value)}
                  label="Bucket"
                >
                  <MenuItem value="">All Buckets</MenuItem>
                  {Object.values(SpeakerBucket).map((bucket) => (
                    <MenuItem key={bucket} value={bucket}>
                      {bucket.replace('_', ' ')}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* Quality Trend Filter */}
            <Grid item xs={12} sm={6} md={3}>
              <FormControl fullWidth size="small">
                <InputLabel>Quality Trend</InputLabel>
                <Select
                  value={localFilters.quality_trend || ''}
                  onChange={(e) => handleFilterChange('quality_trend', e.target.value)}
                  label="Quality Trend"
                >
                  <MenuItem value="">All Trends</MenuItem>
                  {Object.values(QualityTrend).map((trend) => (
                    <MenuItem key={trend} value={trend}>
                      {trend.replace('_', ' ').toUpperCase()}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* SER Score Range */}
            <Grid item xs={12} sm={6} md={3}>
              <TextField
                fullWidth
                size="small"
                label="Min SER Score"
                type="number"
                value={localFilters.min_ser_score || ''}
                onChange={(e) => handleFilterChange('min_ser_score', parseFloat(e.target.value) || undefined)}
                inputProps={{ min: 0, max: 100, step: 0.1 }}
              />
            </Grid>

            <Grid item xs={12} sm={6} md={3}>
              <TextField
                fullWidth
                size="small"
                label="Max SER Score"
                type="number"
                value={localFilters.max_ser_score || ''}
                onChange={(e) => handleFilterChange('max_ser_score', parseFloat(e.target.value) || undefined)}
                inputProps={{ min: 0, max: 100, step: 0.1 }}
              />
            </Grid>

            {/* Data Sufficiency Filter */}
            <Grid item xs={12} sm={6} md={3}>
              <FormControl fullWidth size="small">
                <InputLabel>Data Sufficiency</InputLabel>
                <Select
                  value={localFilters.has_sufficient_data !== undefined ? localFilters.has_sufficient_data.toString() : ''}
                  onChange={(e) => handleFilterChange('has_sufficient_data', 
                    e.target.value === '' ? undefined : e.target.value === 'true'
                  )}
                  label="Data Sufficiency"
                >
                  <MenuItem value="">All</MenuItem>
                  <MenuItem value="true">Sufficient Data</MenuItem>
                  <MenuItem value="false">Insufficient Data</MenuItem>
                </Select>
              </FormControl>
            </Grid>
          </Grid>
        </Paper>
      </Collapse>

      {/* Results Summary */}
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 1 }}>
        <Typography variant="body2" color="text.secondary">
          {loading ? 'Searching...' : `${pagination.totalCount} speakers found`}
          {getActiveFilterCount() > 0 && ` (${getActiveFilterCount()} filters applied)`}
        </Typography>

        {pagination.totalCount > 0 && (
          <Typography variant="body2" color="text.secondary">
            Page {pagination.currentPage} of {pagination.totalPages}
          </Typography>
        )}
      </Box>

      <Divider />
    </Paper>
  );
};

export default SpeakerSearchAndSelection;

/**
 * Speaker Statistics Component
 * 
 * Comprehensive visualization of speaker bucket statistics,
 * quality metrics, and trends with interactive charts.
 */

import React, { useEffect, useMemo } from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  Card,
  CardContent,
  Chip,
  LinearProgress,
  Divider,
  Alert,
  CircularProgress,
  Tooltip,
  IconButton,
} from '@mui/material';
import {
  TrendingUp as TrendingUpIcon,
  TrendingDown as TrendingDownIcon,
  TrendingFlat as TrendingFlatIcon,
  People as PeopleIcon,
  Assessment as AssessmentIcon,
  SwapHoriz as TransitionIcon,
  DataUsage as DataIcon,
  Refresh as RefreshIcon,
} from '@mui/icons-material';
import { useAppDispatch, useAppSelector } from '@/app/hooks';
import {
  getBucketStatistics,
  selectBucketStatistics,
  selectSpeakers,
} from '../speaker-slice';
import { SpeakerBucket, BucketVisualizationData } from '@/domain/types/speaker';

interface SpeakerStatisticsProps {
  compact?: boolean;
  showRefresh?: boolean;
}

export const SpeakerStatistics: React.FC<SpeakerStatisticsProps> = ({
  compact = false,
  showRefresh = true,
}) => {
  const dispatch = useAppDispatch();

  // Redux state
  const bucketStatistics = useAppSelector(selectBucketStatistics);
  const speakers = useAppSelector(selectSpeakers);

  // Load statistics on mount
  useEffect(() => {
    if (!bucketStatistics) {
      dispatch(getBucketStatistics());
    }
  }, [dispatch, bucketStatistics]);

  // Bucket visualization data
  const bucketVisualizationData: BucketVisualizationData[] = useMemo(() => {
    if (!bucketStatistics) return [];

    const total = bucketStatistics.total_speakers;
    const bucketColors = {
      [SpeakerBucket.HIGH_TOUCH]: '#f44336',
      [SpeakerBucket.MEDIUM_TOUCH]: '#ff9800',
      [SpeakerBucket.LOW_TOUCH]: '#2196f3',
      [SpeakerBucket.NO_TOUCH]: '#4caf50',
    };

    return Object.entries(bucketStatistics.bucket_distribution).map(([bucket, count]) => ({
      bucket: bucket as SpeakerBucket,
      count,
      percentage: total > 0 ? (count / total) * 100 : 0,
      averageQuality: bucketStatistics.quality_metrics.average_ser_score || 0,
      trend: 'stable' as any, // Would come from actual trend data
      color: bucketColors[bucket as SpeakerBucket],
    }));
  }, [bucketStatistics]);

  // Handle refresh
  const handleRefresh = () => {
    dispatch(getBucketStatistics());
  };

  if (!bucketStatistics) {
    return (
      <Paper elevation={1} sx={{ p: 3, textAlign: 'center' }}>
        <CircularProgress size={40} />
        <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
          Loading statistics...
        </Typography>
      </Paper>
    );
  }

  return (
    <Paper elevation={1} sx={{ p: compact ? 2 : 3 }}>
      {/* Header */}
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant={compact ? 'h6' : 'h5'} component="h2">
          Speaker Statistics
        </Typography>

        {showRefresh && (
          <Tooltip title="Refresh Statistics">
            <IconButton size="small" onClick={handleRefresh}>
              <RefreshIcon />
            </IconButton>
          </Tooltip>
        )}
      </Box>

      {/* Key Metrics Cards */}
      <Grid container spacing={2} sx={{ mb: 3 }}>
        {/* Total Speakers */}
        <Grid item xs={12} sm={6} md={3}>
          <Card variant="outlined">
            <CardContent sx={{ textAlign: 'center', py: 2 }}>
              <PeopleIcon color="primary" sx={{ fontSize: 40, mb: 1 }} />
              <Typography variant="h4" component="div" fontWeight="bold">
                {bucketStatistics.total_speakers.toLocaleString()}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Total Speakers
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        {/* Average SER Score */}
        <Grid item xs={12} sm={6} md={3}>
          <Card variant="outlined">
            <CardContent sx={{ textAlign: 'center', py: 2 }}>
              <AssessmentIcon color="info" sx={{ fontSize: 40, mb: 1 }} />
              <Typography variant="h4" component="div" fontWeight="bold">
                {bucketStatistics.quality_metrics.average_ser_score.toFixed(1)}%
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Average SER Score
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        {/* Pending Transitions */}
        <Grid item xs={12} sm={6} md={3}>
          <Card variant="outlined">
            <CardContent sx={{ textAlign: 'center', py: 2 }}>
              <TransitionIcon color="warning" sx={{ fontSize: 40, mb: 1 }} />
              <Typography variant="h4" component="div" fontWeight="bold">
                {bucketStatistics.transition_metrics.pending_transitions}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Pending Transitions
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        {/* Data Coverage */}
        <Grid item xs={12} sm={6} md={3}>
          <Card variant="outlined">
            <CardContent sx={{ textAlign: 'center', py: 2 }}>
              <DataIcon color="success" sx={{ fontSize: 40, mb: 1 }} />
              <Typography variant="h4" component="div" fontWeight="bold">
                {bucketStatistics.data_quality.data_coverage_percentage.toFixed(0)}%
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Data Coverage
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Bucket Distribution */}
      <Box sx={{ mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Bucket Distribution
        </Typography>

        <Grid container spacing={2}>
          {bucketVisualizationData.map((bucket) => (
            <Grid item xs={12} sm={6} md={3} key={bucket.bucket}>
              <Card variant="outlined" sx={{ height: '100%' }}>
                <CardContent>
                  <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
                    <Typography variant="subtitle1" fontWeight="medium">
                      {bucket.bucket.replace('_', ' ')}
                    </Typography>
                    <Chip
                      label={`${bucket.percentage.toFixed(1)}%`}
                      size="small"
                      sx={{ backgroundColor: bucket.color, color: 'white' }}
                    />
                  </Box>

                  <Typography variant="h5" fontWeight="bold" sx={{ mb: 1 }}>
                    {bucket.count.toLocaleString()}
                  </Typography>

                  <LinearProgress
                    variant="determinate"
                    value={bucket.percentage}
                    sx={{
                      height: 8,
                      borderRadius: 4,
                      backgroundColor: 'grey.200',
                      '& .MuiLinearProgress-bar': {
                        backgroundColor: bucket.color,
                        borderRadius: 4,
                      },
                    }}
                  />

                  <Typography variant="caption" color="text.secondary" sx={{ mt: 1, display: 'block' }}>
                    {bucket.count} of {bucketStatistics.total_speakers} speakers
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>

      <Divider sx={{ my: 3 }} />

      {/* Quality Metrics */}
      <Box sx={{ mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Quality Metrics
        </Typography>

        <Grid container spacing={2}>
          {/* Quality Distribution */}
          <Grid item xs={12} md={6}>
            <Card variant="outlined">
              <CardContent>
                <Typography variant="subtitle1" fontWeight="medium" gutterBottom>
                  Quality Distribution
                </Typography>

                {Object.entries(bucketStatistics.quality_metrics.quality_distribution).map(([level, count]) => (
                  <Box key={level} sx={{ mb: 1 }}>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
                      <Typography variant="body2" sx={{ textTransform: 'capitalize' }}>
                        {level} Quality
                      </Typography>
                      <Typography variant="body2" fontWeight="medium">
                        {count}
                      </Typography>
                    </Box>
                    <LinearProgress
                      variant="determinate"
                      value={(count / bucketStatistics.total_speakers) * 100}
                      sx={{ height: 6, borderRadius: 3 }}
                    />
                  </Box>
                ))}
              </CardContent>
            </Card>
          </Grid>

          {/* Improvement Trends */}
          <Grid item xs={12} md={6}>
            <Card variant="outlined">
              <CardContent>
                <Typography variant="subtitle1" fontWeight="medium" gutterBottom>
                  Improvement Trends
                </Typography>

                {Object.entries(bucketStatistics.quality_metrics.improvement_trends).map(([trend, count]) => (
                  <Box key={trend} sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 1 }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      {trend === 'improving' && <TrendingUpIcon color="success" fontSize="small" />}
                      {trend === 'declining' && <TrendingDownIcon color="error" fontSize="small" />}
                      {trend === 'stable' && <TrendingFlatIcon color="info" fontSize="small" />}
                      <Typography variant="body2" sx={{ textTransform: 'capitalize' }}>
                        {trend}
                      </Typography>
                    </Box>
                    <Typography variant="body2" fontWeight="medium">
                      {count}
                    </Typography>
                  </Box>
                ))}
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Box>

      {/* Transition Metrics */}
      <Box>
        <Typography variant="h6" gutterBottom>
          Transition Metrics
        </Typography>

        <Grid container spacing={2}>
          <Grid item xs={12} sm={4}>
            <Card variant="outlined">
              <CardContent sx={{ textAlign: 'center' }}>
                <Typography variant="h4" fontWeight="bold" color="warning.main">
                  {bucketStatistics.transition_metrics.pending_transitions}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Pending Requests
                </Typography>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} sm={4}>
            <Card variant="outlined">
              <CardContent sx={{ textAlign: 'center' }}>
                <Typography variant="h4" fontWeight="bold" color="info.main">
                  {bucketStatistics.transition_metrics.recent_transitions}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Recent Transitions
                </Typography>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} sm={4}>
            <Card variant="outlined">
              <CardContent sx={{ textAlign: 'center' }}>
                <Typography variant="h4" fontWeight="bold" color="success.main">
                  {bucketStatistics.transition_metrics.success_rate.toFixed(1)}%
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Success Rate
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Box>

      {/* Data Quality Alert */}
      {bucketStatistics.data_quality.data_coverage_percentage < 80 && (
        <Alert severity="warning" sx={{ mt: 3 }}>
          <Typography variant="body2">
            Data coverage is below 80%. Consider processing more historical data to improve 
            speaker assessment accuracy.
          </Typography>
        </Alert>
      )}
    </Paper>
  );
};

export default SpeakerStatistics;

/**
 * Speaker Table Component
 * 
 * Advanced data table for displaying speakers with sorting, selection,
 * and inline actions. Uses Material-UI DataGrid for performance.
 */

import React, { useMemo, useCallback } from 'react';
import {
  Box,
  Chip,
  IconButton,
  Tooltip,
  Typography,
  LinearProgress,
  Avatar,
  Menu,
  MenuItem,
  ListItemIcon,
  ListItemText,
} from '@mui/material';
import {
  DataGrid,
  GridColDef,
  GridRowSelectionModel,
  GridSortModel,
  GridPaginationModel,
  GridActionsCellItem,
  GridRowParams,
} from '@mui/x-data-grid';
import {
  Visibility as ViewIcon,
  Edit as EditIcon,
  TrendingUp as TrendingUpIcon,
  TrendingDown as TrendingDownIcon,
  TrendingFlat as TrendingFlatIcon,
  MoreVert as MoreVertIcon,
  Assessment as AssessmentIcon,
  SwapHoriz as TransitionIcon,
  History as HistoryIcon,
} from '@mui/icons-material';
import { useAppDispatch, useAppSelector } from '@/app/hooks';
import {
  searchSpeakers,
  setCurrentPage,
  setPageSize,
  setSorting,
  setSelectedSpeakerIds,
  selectSpeakers,
  selectSpeakersLoading,
  selectSelectedSpeakerIds,
  selectPagination,
  selectSearchFilters,
} from '../speaker-slice';
import { Speaker, SpeakerBucket, QualityTrend } from '@/domain/types/speaker';

interface SpeakerTableProps {
  onSpeakerView?: (speaker: Speaker) => void;
  onSpeakerEdit?: (speaker: Speaker) => void;
  onSpeakerAssess?: (speaker: Speaker) => void;
  onTransitionRequest?: (speaker: Speaker) => void;
  onViewHistory?: (speaker: Speaker) => void;
  selectable?: boolean;
  compact?: boolean;
}

export const SpeakerTable: React.FC<SpeakerTableProps> = ({
  onSpeakerView,
  onSpeakerEdit,
  onSpeakerAssess,
  onTransitionRequest,
  onViewHistory,
  selectable = true,
  compact = false,
}) => {
  const dispatch = useAppDispatch();

  // Redux state
  const speakers = useAppSelector(selectSpeakers);
  const loading = useAppSelector(selectSpeakersLoading);
  const selectedSpeakerIds = useAppSelector(selectSelectedSpeakerIds);
  const pagination = useAppSelector(selectPagination);
  const searchFilters = useAppSelector(selectSearchFilters);

  // Bucket color mapping
  const getBucketColor = (bucket: SpeakerBucket): 'error' | 'warning' | 'info' | 'success' => {
    switch (bucket) {
      case SpeakerBucket.HIGH_TOUCH: return 'error';
      case SpeakerBucket.MEDIUM_TOUCH: return 'warning';
      case SpeakerBucket.LOW_TOUCH: return 'info';
      case SpeakerBucket.NO_TOUCH: return 'success';
      default: return 'info';
    }
  };

  // Quality trend icon
  const getTrendIcon = (trend: QualityTrend) => {
    switch (trend) {
      case QualityTrend.IMPROVING:
        return <TrendingUpIcon color="success" fontSize="small" />;
      case QualityTrend.DECLINING:
        return <TrendingDownIcon color="error" fontSize="small" />;
      case QualityTrend.STABLE:
        return <TrendingFlatIcon color="info" fontSize="small" />;
      default:
        return <TrendingFlatIcon color="disabled" fontSize="small" />;
    }
  };

  // Column definitions
  const columns: GridColDef[] = useMemo(() => [
    {
      field: 'speaker_name',
      headerName: 'Speaker',
      width: 200,
      renderCell: (params) => (
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Avatar sx={{ width: 32, height: 32, fontSize: '0.875rem' }}>
            {params.row.speaker_name.charAt(0).toUpperCase()}
          </Avatar>
          <Box>
            <Typography variant="body2" fontWeight="medium">
              {params.row.speaker_name}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              {params.row.speaker_identifier}
            </Typography>
          </Box>
        </Box>
      ),
    },
    {
      field: 'current_bucket',
      headerName: 'Bucket',
      width: 130,
      renderCell: (params) => (
        <Chip
          label={params.value.replace('_', ' ')}
          color={getBucketColor(params.value)}
          size="small"
          variant="outlined"
        />
      ),
    },
    {
      field: 'note_count',
      headerName: 'Notes',
      width: 80,
      type: 'number',
      renderCell: (params) => (
        <Typography variant="body2">
          {params.value.toLocaleString()}
        </Typography>
      ),
    },
    {
      field: 'average_ser_score',
      headerName: 'Avg SER',
      width: 100,
      type: 'number',
      renderCell: (params) => (
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Typography variant="body2" fontWeight="medium">
            {params.value.toFixed(1)}%
          </Typography>
          <LinearProgress
            variant="determinate"
            value={Math.min(params.value, 100)}
            sx={{ 
              width: 40, 
              height: 4,
              backgroundColor: 'grey.200',
              '& .MuiLinearProgress-bar': {
                backgroundColor: params.value > 25 ? 'error.main' : 
                                params.value > 15 ? 'warning.main' : 'success.main'
              }
            }}
          />
        </Box>
      ),
    },
    {
      field: 'quality_trend',
      headerName: 'Trend',
      width: 80,
      renderCell: (params) => (
        <Tooltip title={params.value.replace('_', ' ').toUpperCase()}>
          <Box sx={{ display: 'flex', justifyContent: 'center' }}>
            {getTrendIcon(params.value)}
          </Box>
        </Tooltip>
      ),
    },
    {
      field: 'has_sufficient_data',
      headerName: 'Data',
      width: 80,
      renderCell: (params) => (
        <Chip
          label={params.value ? 'Sufficient' : 'Limited'}
          color={params.value ? 'success' : 'warning'}
          size="small"
          variant="outlined"
        />
      ),
    },
    {
      field: 'should_transition',
      headerName: 'Transition',
      width: 100,
      renderCell: (params) => (
        params.value ? (
          <Chip
            label="Recommended"
            color="info"
            size="small"
            icon={<TransitionIcon />}
          />
        ) : null
      ),
    },
    {
      field: 'updated_at',
      headerName: 'Last Updated',
      width: 120,
      renderCell: (params) => (
        <Typography variant="caption" color="text.secondary">
          {new Date(params.value).toLocaleDateString()}
        </Typography>
      ),
    },
    {
      field: 'actions',
      type: 'actions',
      headerName: 'Actions',
      width: 120,
      getActions: (params: GridRowParams) => {
        const actions = [
          <GridActionsCellItem
            icon={<ViewIcon />}
            label="View Details"
            onClick={() => onSpeakerView?.(params.row)}
          />,
        ];

        if (onSpeakerEdit) {
          actions.push(
            <GridActionsCellItem
              icon={<EditIcon />}
              label="Edit"
              onClick={() => onSpeakerEdit(params.row)}
            />
          );
        }

        if (onSpeakerAssess) {
          actions.push(
            <GridActionsCellItem
              icon={<AssessmentIcon />}
              label="Assess"
              onClick={() => onSpeakerAssess(params.row)}
            />
          );
        }

        return actions;
      },
    },
  ], [onSpeakerView, onSpeakerEdit, onSpeakerAssess]);

  // Handle selection change
  const handleSelectionChange = useCallback((newSelection: GridRowSelectionModel) => {
    dispatch(setSelectedSpeakerIds(newSelection as string[]));
  }, [dispatch]);

  // Handle sort change
  const handleSortChange = useCallback((sortModel: GridSortModel) => {
    if (sortModel.length > 0) {
      const { field, sort } = sortModel[0];
      dispatch(setSorting({ sortBy: field, sortOrder: sort as 'asc' | 'desc' }));

      // Trigger new search with sorting
      dispatch(searchSpeakers({
        ...searchFilters,
        page: pagination.currentPage,
        page_size: pagination.pageSize,
        sort_by: field,
        sort_order: sort as 'asc' | 'desc',
      }));
    }
  }, [dispatch, searchFilters, pagination]);

  // Handle pagination change
  const handlePaginationChange = useCallback((paginationModel: GridPaginationModel) => {
    const { page, pageSize } = paginationModel;

    if (pageSize !== pagination.pageSize) {
      dispatch(setPageSize(pageSize));
    }

    if (page + 1 !== pagination.currentPage) {
      dispatch(setCurrentPage(page + 1));
    }

    // Trigger new search with pagination
    dispatch(searchSpeakers({
      ...searchFilters,
      page: page + 1,
      page_size: pageSize,
    }));
  }, [dispatch, searchFilters, pagination]);

  // Handle row double click
  const handleRowDoubleClick = useCallback((params: GridRowParams) => {
    onSpeakerView?.(params.row);
  }, [onSpeakerView]);

  return (
    <Box sx={{ height: compact ? 400 : 600, width: '100%' }}>
      <DataGrid
        rows={speakers}
        columns={columns}
        getRowId={(row) => row.speaker_id}
        loading={loading}

        // Selection
        checkboxSelection={selectable}
        rowSelectionModel={selectedSpeakerIds}
        onRowSelectionModelChange={handleSelectionChange}

        // Sorting
        sortingMode="server"
        onSortModelChange={handleSortChange}

        // Pagination
        paginationMode="server"
        paginationModel={{
          page: pagination.currentPage - 1, // DataGrid uses 0-based indexing
          pageSize: pagination.pageSize,
        }}
        onPaginationModelChange={handlePaginationChange}
        rowCount={pagination.totalCount}
        pageSizeOptions={[25, 50, 100]}

        // Row interaction
        onRowDoubleClick={handleRowDoubleClick}

        // Styling
        density={compact ? 'compact' : 'standard'}
        disableRowSelectionOnClick
        sx={{
          '& .MuiDataGrid-row:hover': {
            backgroundColor: 'action.hover',
          },
          '& .MuiDataGrid-cell:focus': {
            outline: 'none',
          },
        }}

        // Loading overlay
        slots={{
          loadingOverlay: () => (
            <Box sx={{ 
              display: 'flex', 
              alignItems: 'center', 
              justifyContent: 'center',
              height: '100%',
              flexDirection: 'column',
              gap: 2
            }}>
              <LinearProgress sx={{ width: 200 }} />
              <Typography variant="body2" color="text.secondary">
                Loading speakers...
              </Typography>
            </Box>
          ),
        }}
      />
    </Box>
  );
};

