      </Box>

      {/* Main Content */}
      <Box sx={{ width: '100%' }}>
        {/* Tabs */}
        <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
          <Tabs value={currentTab} onChange={handleTabChange} aria-label="speaker management tabs">
            <Tab 
              label="Speaker Directory" 
