// State interface
interface SpeakerState {
  // Speaker list and search
  speakers: Speaker[];
  totalCount: number;
  currentPage: number;
  pageSize: number;
  totalPages: number;
  searchFilters: SpeakerSearchFilters;
  quickFilters: QuickFilterOption[];

  // Selected speaker details
  selectedSpeaker: Speaker | null;
  comprehensiveView: ComprehensiveSpeakerView | null;

  // Statistics and analytics
  bucketStatistics: SpeakerBucketStats | null;
  speakersNeedingTransition: Speaker[];

  // Transition requests
  transitionRequests: BucketTransitionRequest[];
  pendingTransitions: BucketTransitionRequest[];

  // UI state
  selectedSpeakerIds: string[];
  sortBy: string;
  sortOrder: 'asc' | 'desc';
  viewMode: 'table' | 'grid' | 'analytics';

  // Loading states
  loading: {
    speakers: boolean;
    speakerDetails: boolean;
    statistics: boolean;
    transitions: boolean;
    creating: boolean;
    updating: boolean;
  };

  // Error states
  error: {
    speakers: string | null;
    speakerDetails: string | null;
    statistics: string | null;
    transitions: string | null;
    general: string | null;
  };
}

// Initial state
const initialState: SpeakerState = {
  speakers: [],
  totalCount: 0,
  currentPage: 1,
  pageSize: 50,
  totalPages: 0,
  searchFilters: {},
  quickFilters: [],

  selectedSpeaker: null,
  comprehensiveView: null,

  bucketStatistics: null,
  speakersNeedingTransition: [],

  transitionRequests: [],
  pendingTransitions: [],

  selectedSpeakerIds: [],
  sortBy: 'speaker_name',
  sortOrder: 'asc',
  viewMode: 'table',

  loading: {
    speakers: false,
    speakerDetails: false,
    statistics: false,
    transitions: false,
    creating: false,
    updating: false,
  },

  error: {
    speakers: null,
    speakerDetails: null,
    statistics: null,
    transitions: null,
    general: null,
  },
};

// Slice
const speakerSlice = createSlice({
  name: 'speakers',
  initialState,
  reducers: {
    // Search and filtering
    setSearchFilters: (state, action: PayloadAction<SpeakerSearchFilters>) => {
      state.searchFilters = action.payload;
      state.currentPage = 1; // Reset to first page when filters change
    },

    clearSearchFilters: (state) => {
      state.searchFilters = {};
      state.currentPage = 1;
    },

    setCurrentPage: (state, action: PayloadAction<number>) => {
      state.currentPage = action.payload;
    },

    setPageSize: (state, action: PayloadAction<number>) => {
      state.pageSize = action.payload;
      state.currentPage = 1; // Reset to first page when page size changes
    },

    // Sorting
    setSorting: (state, action: PayloadAction<{ sortBy: string; sortOrder: 'asc' | 'desc' }>) => {
      state.sortBy = action.payload.sortBy;
      state.sortOrder = action.payload.sortOrder;
    },

    // Selection
    setSelectedSpeakerIds: (state, action: PayloadAction<string[]>) => {
      state.selectedSpeakerIds = action.payload;
    },

    toggleSpeakerSelection: (state, action: PayloadAction<string>) => {
      const speakerId = action.payload;
      const index = state.selectedSpeakerIds.indexOf(speakerId);

      if (index > -1) {
        state.selectedSpeakerIds.splice(index, 1);
      } else {
        state.selectedSpeakerIds.push(speakerId);
      }
    },

    selectAllSpeakers: (state) => {
      state.selectedSpeakerIds = state.speakers.map(speaker => speaker.speaker_id);
    },

    clearSelection: (state) => {
      state.selectedSpeakerIds = [];
    },

    // View mode
    setViewMode: (state, action: PayloadAction<'table' | 'grid' | 'analytics'>) => {
      state.viewMode = action.payload;
    },

    // Selected speaker
    setSelectedSpeaker: (state, action: PayloadAction<Speaker | null>) => {
      state.selectedSpeaker = action.payload;
    },

    // Clear errors
    clearErrors: (state) => {
      state.error = {
        speakers: null,
        speakerDetails: null,
        statistics: null,
        transitions: null,
        general: null,
      };
    },

    clearError: (state, action: PayloadAction<keyof SpeakerState['error']>) => {
      state.error[action.payload] = null;
    },
  },

  extraReducers: (builder) => {
    // Search speakers
    builder
      .addCase(searchSpeakers.pending, (state) => {
        state.loading.speakers = true;
        state.error.speakers = null;
      })
      .addCase(searchSpeakers.fulfilled, (state, action) => {
        state.loading.speakers = false;
        state.speakers = action.payload.speakers;
        state.totalCount = action.payload.total_count;
        state.currentPage = action.payload.page;
        state.pageSize = action.payload.page_size;
        state.totalPages = action.payload.total_pages;
      })
      .addCase(searchSpeakers.rejected, (state, action) => {
        state.loading.speakers = false;
        state.error.speakers = action.error.message || 'Failed to search speakers';
      });

    // Get speaker by ID
    builder
      .addCase(getSpeakerById.pending, (state) => {
        state.loading.speakerDetails = true;
        state.error.speakerDetails = null;
      })
      .addCase(getSpeakerById.fulfilled, (state, action) => {
        state.loading.speakerDetails = false;
        state.selectedSpeaker = action.payload;
      })
      .addCase(getSpeakerById.rejected, (state, action) => {
        state.loading.speakerDetails = false;
        state.error.speakerDetails = action.error.message || 'Failed to get speaker details';
      });

    // Create speaker
    builder
      .addCase(createSpeaker.pending, (state) => {
        state.loading.creating = true;
        state.error.general = null;
      })
      .addCase(createSpeaker.fulfilled, (state, action) => {
        state.loading.creating = false;
        state.speakers.unshift(action.payload);
        state.totalCount += 1;
      })
      .addCase(createSpeaker.rejected, (state, action) => {
        state.loading.creating = false;
        state.error.general = action.error.message || 'Failed to create speaker';
      });

    // Update speaker
    builder
      .addCase(updateSpeaker.pending, (state) => {
        state.loading.updating = true;
        state.error.general = null;
      })
      .addCase(updateSpeaker.fulfilled, (state, action) => {
        state.loading.updating = false;
        const index = state.speakers.findIndex(s => s.speaker_id === action.payload.speaker_id);
        if (index > -1) {
          state.speakers[index] = action.payload;
        }
        if (state.selectedSpeaker?.speaker_id === action.payload.speaker_id) {
          state.selectedSpeaker = action.payload;
        }
      })
      .addCase(updateSpeaker.rejected, (state, action) => {
        state.loading.updating = false;
        state.error.general = action.error.message || 'Failed to update speaker';
      });

    // Get bucket statistics
    builder
      .addCase(getBucketStatistics.pending, (state) => {
        state.loading.statistics = true;
        state.error.statistics = null;
      })
      .addCase(getBucketStatistics.fulfilled, (state, action) => {
        state.loading.statistics = false;
        state.bucketStatistics = action.payload;
      })
      .addCase(getBucketStatistics.rejected, (state, action) => {
        state.loading.statistics = false;
        state.error.statistics = action.error.message || 'Failed to get bucket statistics';
      });

    // Get speakers needing transition
    builder
      .addCase(getSpeakersNeedingTransition.fulfilled, (state, action) => {
        state.speakersNeedingTransition = action.payload;
      });

    // Get pending transition requests
    builder
      .addCase(getPendingTransitionRequests.pending, (state) => {
        state.loading.transitions = true;
        state.error.transitions = null;
      })
      .addCase(getPendingTransitionRequests.fulfilled, (state, action) => {
        state.loading.transitions = false;
        state.pendingTransitions = action.payload;
      })
      .addCase(getPendingTransitionRequests.rejected, (state, action) => {
        state.loading.transitions = false;
        state.error.transitions = action.error.message || 'Failed to get pending transitions';
      });

    // Get comprehensive speaker view
    builder
      .addCase(getComprehensiveSpeakerView.pending, (state) => {
        state.loading.speakerDetails = true;
        state.error.speakerDetails = null;
      })
      .addCase(getComprehensiveSpeakerView.fulfilled, (state, action) => {
        state.loading.speakerDetails = false;
        state.comprehensiveView = action.payload;
        state.selectedSpeaker = action.payload.speaker;
      })
      .addCase(getComprehensiveSpeakerView.rejected, (state, action) => {
        state.loading.speakerDetails = false;
        state.error.speakerDetails = action.error.message || 'Failed to get comprehensive view';
      });

    // Get quick filter options
    builder
      .addCase(getQuickFilterOptions.fulfilled, (state, action) => {
        state.quickFilters = action.payload;
      });
  },
});

// Export actions
export const {
  setSearchFilters,
  clearSearchFilters,
  setCurrentPage,
  setPageSize,
  setSorting,
  setSelectedSpeakerIds,
  toggleSpeakerSelection,
  selectAllSpeakers,
  clearSelection,
  setViewMode,
  setSelectedSpeaker,
  clearErrors,
  clearError,
} = speakerSlice.actions;

// Selectors
export const selectSpeakers = (state: { speakers: SpeakerState }) => state.speakers.speakers;
export const selectSpeakersLoading = (state: { speakers: SpeakerState }) => state.speakers.loading.speakers;
export const selectSpeakersError = (state: { speakers: SpeakerState }) => state.speakers.error.speakers;
export const selectSelectedSpeaker = (state: { speakers: SpeakerState }) => state.speakers.selectedSpeaker;
export const selectComprehensiveView = (state: { speakers: SpeakerState }) => state.speakers.comprehensiveView;
export const selectBucketStatistics = (state: { speakers: SpeakerState }) => state.speakers.bucketStatistics;
export const selectSearchFilters = (state: { speakers: SpeakerState }) => state.speakers.searchFilters;
export const selectSelectedSpeakerIds = (state: { speakers: SpeakerState }) => state.speakers.selectedSpeakerIds;
export const selectPagination = (state: { speakers: SpeakerState }) => ({
  currentPage: state.speakers.currentPage,
  pageSize: state.speakers.pageSize,
  totalCount: state.speakers.totalCount,
  totalPages: state.speakers.totalPages,
});
export const selectViewMode = (state: { speakers: SpeakerState }) => state.speakers.viewMode;
export const selectQuickFilters = (state: { speakers: SpeakerState }) => state.speakers.quickFilters;
export const selectPendingTransitions = (state: { speakers: SpeakerState }) => state.speakers.pendingTransitions;

export default speakerSlice.reducer;

/**
 * Diagnostics Panel Component
 * Displays detailed diagnostic information for a specific service
 */

import React from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Chip,
  Divider,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  IconButton,
  Tooltip,
} from '@mui/material';
import {
  Close as CloseIcon,
  CheckCircle as SuccessIcon,
  Error as ErrorIcon,
  Warning as WarningIcon,
  NetworkCheck as NetworkIcon,
  Speed as PerformanceIcon,
  Storage as DependencyIcon,
  Info as InfoIcon,
  Dns as DnsIcon,
  Router as PortIcon,
  Security as SslIcon,
} from '@mui/icons-material';
import { SERVICE_CONFIGS } from '@infrastructure/api/health-api';
import type { DiagnosticsPanelProps, HealthStatus } from '../types/health-types';

const DiagnosticsPanel: React.FC<DiagnosticsPanelProps> = ({
  service,
  diagnostics,
  onClose,
}) => {
  const config = SERVICE_CONFIGS[service];

  const getStatusIcon = (status: boolean | HealthStatus) => {
    if (typeof status === 'boolean') {
      return status ? <SuccessIcon color="success" /> : <ErrorIcon color="error" />;
    }

    switch (status) {
      case 'healthy':
        return <SuccessIcon color="success" />;
      case 'degraded':
        return <WarningIcon color="warning" />;
      case 'error':
        return <ErrorIcon color="error" />;
      default:
        return <WarningIcon color="disabled" />;
    }
  };

  const getStatusColor = (status: boolean | HealthStatus) => {
    if (typeof status === 'boolean') {
      return status ? 'success' : 'error';
    }

    switch (status) {
      case 'healthy':
        return 'success';
      case 'degraded':
        return 'warning';
      case 'error':
        return 'error';
      default:
        return 'default';
    }
  };

  const getStatusText = (status: boolean | HealthStatus) => {
    if (typeof status === 'boolean') {
      return status ? 'OK' : 'Failed';
    }

    switch (status) {
      case 'healthy':
        return 'Healthy';
      case 'degraded':
        return 'Degraded';
      case 'error':
        return 'Error';
      default:
        return 'Unknown';
    }
  };

  const getPerformanceRating = (responseTime: number) => {
    if (!config) return 'Unknown';

    if (responseTime <= config.expectedResponseTime) return 'Excellent';
    if (responseTime <= config.criticalResponseTime) return 'Good';
    return 'Poor';
  };

  const getPerformanceColor = (responseTime: number) => {
    if (!config) return 'default';

    if (responseTime <= config.expectedResponseTime) return 'success';
    if (responseTime <= config.criticalResponseTime) return 'warning';
    return 'error';
  };

  return (
    <Dialog
      open={true}
      onClose={onClose}
      maxWidth="md"
      fullWidth
      PaperProps={{
        sx: { minHeight: '60vh' }
      }}
    >
      <DialogTitle>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Typography variant="h6">
            Diagnostics: {config?.displayName || service}
          </Typography>

          <Tooltip title="Close">
            <IconButton onClick={onClose} size="small">
              <CloseIcon />
            </IconButton>
          </Tooltip>
        </Box>
      </DialogTitle>

      <DialogContent>
        <Grid container spacing={3}>
          {/* Connectivity Tests */}
          <Grid item xs={12} md={6}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
                  <NetworkIcon color="primary" />
                  <Typography variant="h6">Connectivity</Typography>
                </Box>

                <List dense>
                  <ListItem>
                    <ListItemIcon>
                      {getStatusIcon(diagnostics.connectivity.can_reach)}
                    </ListItemIcon>
                    <ListItemText
                      primary="Service Reachable"
                      secondary={diagnostics.connectivity.can_reach ? 
                        'Service is responding to requests' : 
                        'Cannot connect to service'}
                    />
                    <Chip
                      label={getStatusText(diagnostics.connectivity.can_reach)}
                      color={getStatusColor(diagnostics.connectivity.can_reach) as any}
                      size="small"
                    />
                  </ListItem>

                  <ListItem>
                    <ListItemIcon>
                      <DnsIcon color={diagnostics.connectivity.dns_resolution ? 'success' : 'error'} />
                    </ListItemIcon>
                    <ListItemText
                      primary="DNS Resolution"
                      secondary={diagnostics.connectivity.dns_resolution ? 
                        'Hostname resolves correctly' : 
                        'DNS resolution failed'}
                    />
                    <Chip
                      label={getStatusText(diagnostics.connectivity.dns_resolution)}
                      color={getStatusColor(diagnostics.connectivity.dns_resolution) as any}
                      size="small"
                    />
                  </ListItem>

                  <ListItem>
                    <ListItemIcon>
                      <PortIcon color={diagnostics.connectivity.port_open ? 'success' : 'error'} />
                    </ListItemIcon>
                    <ListItemText
                      primary="Port Accessible"
                      secondary={diagnostics.connectivity.port_open ? 
                        `Port ${config?.port} is open and accessible` : 
                        `Port ${config?.port} is not accessible`}
                    />
                    <Chip
                      label={getStatusText(diagnostics.connectivity.port_open)}
                      color={getStatusColor(diagnostics.connectivity.port_open) as any}
                      size="small"
                    />
                  </ListItem>

                  {diagnostics.connectivity.ssl_valid !== undefined && (
                    <ListItem>
                      <ListItemIcon>
                        <SslIcon color={diagnostics.connectivity.ssl_valid ? 'success' : 'error'} />
                      </ListItemIcon>
                      <ListItemText
                        primary="SSL Certificate"
                        secondary={diagnostics.connectivity.ssl_valid ? 
                          'SSL certificate is valid' : 
                          'SSL certificate is invalid or expired'}
                      />
                      <Chip
                        label={getStatusText(diagnostics.connectivity.ssl_valid)}
                        color={getStatusColor(diagnostics.connectivity.ssl_valid) as any}
                        size="small"
                      />
                    </ListItem>
                  )}
                </List>
              </CardContent>
            </Card>
          </Grid>

          {/* Performance Metrics */}
          <Grid item xs={12} md={6}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
                  <PerformanceIcon color="primary" />
                  <Typography variant="h6">Performance</Typography>
                </Box>

                <Box sx={{ mb: 2 }}>
                  <Typography variant="body2" color="text.secondary" gutterBottom>
                    Response Time
                  </Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <Typography variant="h5">
                      {diagnostics.performance.response_time}ms
                    </Typography>
                    <Chip
                      label={getPerformanceRating(diagnostics.performance.response_time)}
                      color={getPerformanceColor(diagnostics.performance.response_time) as any}
                      size="small"
                    />
                  </Box>

                  {config && (
                    <Typography variant="caption" color="text.secondary">
                      Expected: {config.expectedResponseTime}ms, Critical: {config.criticalResponseTime}ms
                    </Typography>
                  )}
                </Box>

                <Divider sx={{ my: 2 }} />

                <Box sx={{ mb: 2 }}>
                  <Typography variant="body2" color="text.secondary" gutterBottom>
                    Error Rate
                  </Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <Typography variant="h5">
                      {diagnostics.performance.error_rate.toFixed(1)}%
                    </Typography>
                    <Chip
                      label={diagnostics.performance.error_rate === 0 ? 'Excellent' : 
                             diagnostics.performance.error_rate < 5 ? 'Good' : 'High'}
                      color={diagnostics.performance.error_rate === 0 ? 'success' : 
                             diagnostics.performance.error_rate < 5 ? 'warning' : 'error'}
                      size="small"
                    />
                  </Box>
                </Box>

                {diagnostics.performance.throughput && (
                  <Box>
                    <Typography variant="body2" color="text.secondary" gutterBottom>
                      Throughput
                    </Typography>
                    <Typography variant="h6">
                      {diagnostics.performance.throughput} req/sec
                    </Typography>
                  </Box>
                )}
              </CardContent>
            </Card>
          </Grid>

          {/* Dependencies */}
          <Grid item xs={12}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
                  <DependencyIcon color="primary" />
                  <Typography variant="h6">Dependencies</Typography>
                </Box>

                <Grid container spacing={2}>
                  {diagnostics.dependencies.database && (
                    <Grid item xs={12} sm={6} md={4}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        {getStatusIcon(diagnostics.dependencies.database)}
                        <Typography variant="body1">Database</Typography>
                        <Chip
                          label={getStatusText(diagnostics.dependencies.database)}
                          color={getStatusColor(diagnostics.dependencies.database) as any}
                          size="small"
                        />
                      </Box>
                    </Grid>
                  )}

                  {diagnostics.dependencies.cache && (
                    <Grid item xs={12} sm={6} md={4}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        {getStatusIcon(diagnostics.dependencies.cache)}
                        <Typography variant="body1">Cache</Typography>
                        <Chip
                          label={getStatusText(diagnostics.dependencies.cache)}
                          color={getStatusColor(diagnostics.dependencies.cache) as any}
                          size="small"
                        />
                      </Box>
                    </Grid>
                  )}

                  {diagnostics.dependencies.external_apis && 
                    Object.entries(diagnostics.dependencies.external_apis).map(([api, status]) => (
                      <Grid item xs={12} sm={6} md={4} key={api}>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          {getStatusIcon(status)}
                          <Typography variant="body1">{api}</Typography>
                          <Chip
                            label={getStatusText(status)}
                            color={getStatusColor(status) as any}
                            size="small"
                          />
                        </Box>
                      </Grid>
                    ))
                  }
                </Grid>

                {!diagnostics.dependencies.database && 
                 !diagnostics.dependencies.cache && 
                 !diagnostics.dependencies.external_apis && (
                  <Typography variant="body2" color="text.secondary">
                    No dependency information available
                  </Typography>
                )}
              </CardContent>
            </Card>
          </Grid>

          {/* System Information */}
          {diagnostics.system_info && (
            <Grid item xs={12}>
              <Card>
                <CardContent>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
                    <InfoIcon color="primary" />
                    <Typography variant="h6">System Information</Typography>
                  </Box>

                  <Grid container spacing={2}>
                    <Grid item xs={12} sm={4}>
                      <Typography variant="body2" color="text.secondary">
                        Version
                      </Typography>
                      <Typography variant="body1">
                        {diagnostics.system_info.version}
                      </Typography>
                    </Grid>

                    <Grid item xs={12} sm={4}>
                      <Typography variant="body2" color="text.secondary">
                        Uptime
                      </Typography>
                      <Typography variant="body1">
                        {diagnostics.system_info.uptime}
                      </Typography>
                    </Grid>

                    <Grid item xs={12} sm={4}>
                      <Typography variant="body2" color="text.secondary">
                        Environment
                      </Typography>
                      <Typography variant="body1">
                        {diagnostics.system_info.environment}
                      </Typography>
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Grid>
          )}
        </Grid>
      </DialogContent>

      <DialogActions>
        <Button onClick={onClose} variant="contained">
          Close
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default DiagnosticsPanel;

/**
 * Health Overview Component
 * Displays system-wide health metrics and summary
 */

import React from 'react';
import {
  Card,
  CardContent,
  CardHeader,
  Typography,
  Box,
  Grid,
  LinearProgress,
  Chip,
  IconButton,
  Tooltip,
  CircularProgress,
} from '@mui/material';
import {
  Refresh as RefreshIcon,
  CheckCircle as HealthyIcon,
  Warning as WarningIcon,
  Error as ErrorIcon,
  Speed as SpeedIcon,
  Timeline as UptimeIcon,
  Storage as ServicesIcon,
} from '@mui/icons-material';
import type { HealthOverviewProps, HealthStatus } from '../types/health-types';

const HealthOverview: React.FC<HealthOverviewProps> = ({
  overview,
  onRefresh,
  loading = false,
}) => {
  const getOverallStatusIcon = (status: HealthStatus) => {
    switch (status) {
      case 'healthy':
        return <HealthyIcon color="success" sx={{ fontSize: 32 }} />;
      case 'degraded':
        return <WarningIcon color="warning" sx={{ fontSize: 32 }} />;
      case 'error':
        return <ErrorIcon color="error" sx={{ fontSize: 32 }} />;
      default:
        return <ServicesIcon color="disabled" sx={{ fontSize: 32 }} />;
    }
  };

  const getOverallStatusColor = (status: HealthStatus) => {
    switch (status) {
      case 'healthy':
        return 'success';
      case 'degraded':
        return 'warning';
      case 'error':
        return 'error';
      default:
        return 'default';
    }
  };

  const getOverallStatusText = (status: HealthStatus) => {
    switch (status) {
      case 'healthy':
        return 'All Systems Operational';
      case 'degraded':
        return 'Some Issues Detected';
      case 'error':
        return 'Critical Issues Present';
      default:
        return 'Status Unknown';
    }
  };

  const getHealthPercentage = () => {
    if (overview.total_services === 0) return 0;
    return (overview.healthy_services / overview.total_services) * 100;
  };

  const getUptimeColor = (uptime: number) => {
    if (uptime >= 99.9) return 'success';
    if (uptime >= 99.0) return 'warning';
    return 'error';
  };

  const getResponseTimeColor = (responseTime: number) => {
    if (responseTime <= 200) return 'success';
    if (responseTime <= 500) return 'warning';
    return 'error';
  };

  const formatLastUpdated = (timestamp: string) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffSeconds = Math.floor(diffMs / 1000);

    if (diffSeconds < 60) {
      return `${diffSeconds} seconds ago`;
    } else if (diffSeconds < 3600) {
      return `${Math.floor(diffSeconds / 60)} minutes ago`;
    } else {
      return date.toLocaleTimeString();
    }
  };

  return (
    <Card>
      <CardHeader
        title="System Health Overview"
        action={
          onRefresh && (
            <Tooltip title="Refresh">
              <IconButton onClick={onRefresh} disabled={loading}>
                {loading ? <CircularProgress size={20} /> : <RefreshIcon />}
              </IconButton>
            </Tooltip>
          )
        }
      />

      <CardContent>
        {/* Overall Status */}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 3 }}>
          {getOverallStatusIcon(overview.overall_status)}

          <Box sx={{ flexGrow: 1 }}>
            <Typography variant="h5" component="h3" gutterBottom>
              {getOverallStatusText(overview.overall_status)}
            </Typography>

            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Chip
                label={`${overview.healthy_services}/${overview.total_services} Services Healthy`}
                color={getOverallStatusColor(overview.overall_status) as any}
                variant="outlined"
              />

              <Typography variant="body2" color="text.secondary">
                Last updated: {formatLastUpdated(overview.last_updated)}
              </Typography>
            </Box>
          </Box>
        </Box>

        {/* Health Progress Bar */}
        <Box sx={{ mb: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
            <Typography variant="body2" color="text.secondary">
              System Health
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {getHealthPercentage().toFixed(1)}%
            </Typography>
          </Box>
          <LinearProgress
            variant="determinate"
            value={getHealthPercentage()}
            color={getOverallStatusColor(overview.overall_status) as any}
            sx={{ height: 8, borderRadius: 4 }}
          />
        </Box>

        {/* Metrics Grid */}
        <Grid container spacing={3}>
          {/* Service Status Breakdown */}
          <Grid item xs={12} md={3}>
            <Box sx={{ textAlign: 'center' }}>
              <ServicesIcon color="action" sx={{ fontSize: 24, mb: 1 }} />
              <Typography variant="h6" component="div">
                {overview.total_services}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Total Services
              </Typography>

              <Box sx={{ mt: 2, space: 1 }}>
                {overview.healthy_services > 0 && (
                  <Chip
                    label={`${overview.healthy_services} Healthy`}
                    color="success"
                    size="small"
                    variant="outlined"
                    sx={{ mb: 0.5, mr: 0.5 }}
                  />
                )}

                {overview.degraded_services > 0 && (
                  <Chip
                    label={`${overview.degraded_services} Degraded`}
                    color="warning"
                    size="small"
                    variant="outlined"
                    sx={{ mb: 0.5, mr: 0.5 }}
                  />
                )}

                {overview.error_services > 0 && (
                  <Chip
                    label={`${overview.error_services} Error`}
                    color="error"
                    size="small"
                    variant="outlined"
                    sx={{ mb: 0.5, mr: 0.5 }}
                  />
                )}

                {overview.unknown_services > 0 && (
                  <Chip
                    label={`${overview.unknown_services} Unknown`}
                    color="default"
                    size="small"
                    variant="outlined"
                    sx={{ mb: 0.5, mr: 0.5 }}
                  />
                )}
              </Box>
            </Box>
          </Grid>

          {/* Average Response Time */}
          <Grid item xs={12} md={3}>
            <Box sx={{ textAlign: 'center' }}>
              <SpeedIcon 
                color={getResponseTimeColor(overview.average_response_time) as any} 
                sx={{ fontSize: 24, mb: 1 }} 
              />
              <Typography variant="h6" component="div">
                {overview.average_response_time}ms
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Avg Response Time
              </Typography>

              <Box sx={{ mt: 1 }}>
                <Chip
                  label={overview.average_response_time <= 200 ? 'Excellent' : 
                         overview.average_response_time <= 500 ? 'Good' : 'Slow'}
                  color={getResponseTimeColor(overview.average_response_time) as any}
                  size="small"
                  variant="outlined"
                />
              </Box>
            </Box>
          </Grid>

          {/* Overall Uptime */}
          <Grid item xs={12} md={3}>
            <Box sx={{ textAlign: 'center' }}>
              <UptimeIcon 
                color={getUptimeColor(overview.overall_uptime) as any} 
                sx={{ fontSize: 24, mb: 1 }} 
              />
              <Typography variant="h6" component="div">
                {overview.overall_uptime.toFixed(2)}%
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Overall Uptime
              </Typography>

              <Box sx={{ mt: 1 }}>
                <Chip
                  label={overview.overall_uptime >= 99.9 ? 'Excellent' : 
                         overview.overall_uptime >= 99.0 ? 'Good' : 'Poor'}
                  color={getUptimeColor(overview.overall_uptime) as any}
                  size="small"
                  variant="outlined"
                />
              </Box>
            </Box>
          </Grid>

          {/* System Status */}
          <Grid item xs={12} md={3}>
            <Box sx={{ textAlign: 'center' }}>
              {getOverallStatusIcon(overview.overall_status)}
              <Typography variant="h6" component="div" sx={{ mt: 1 }}>
                {overview.overall_status.charAt(0).toUpperCase() + overview.overall_status.slice(1)}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                System Status
              </Typography>

              <Box sx={{ mt: 1 }}>
                <Chip
                  label={getOverallStatusText(overview.overall_status)}
                  color={getOverallStatusColor(overview.overall_status) as any}
                  size="small"
                  variant="filled"
                />
              </Box>
            </Box>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};

export default HealthOverview;

/**
 * Service Status Card Component
 * Displays individual service health status with detailed metrics
 */

import React from 'react';
import {
  Card,
  CardContent,
  CardActions,
  Typography,
  Box,
  Chip,
  IconButton,
  Button,
  LinearProgress,
  Tooltip,
  Divider,
} from '@mui/material';
import {
  CheckCircle as HealthyIcon,
  Warning as DegradedIcon,
  Error as ErrorIcon,
  Help as UnknownIcon,
  Refresh as RefreshIcon,
  Timeline as DiagnosticsIcon,
  AccessTime as TimeIcon,
  Speed as ResponseIcon,
} from '@mui/icons-material';
import { SERVICE_CONFIGS } from '@infrastructure/api/health-api';
import type { ServiceStatusCardProps, HealthStatus } from '../types/health-types';

const ServiceStatusCard: React.FC<ServiceStatusCardProps> = ({
  service,
  health,
  onRefresh,
  onViewDiagnostics,
}) => {
  const config = SERVICE_CONFIGS[service];

  if (!config) {
    return null;
  }

  const getStatusIcon = (status: HealthStatus) => {
    switch (status) {
      case 'healthy':
        return <HealthyIcon color="success" />;
      case 'degraded':
        return <DegradedIcon color="warning" />;
      case 'error':
        return <ErrorIcon color="error" />;
      default:
        return <UnknownIcon color="disabled" />;
    }
  };

  const getStatusColor = (status: HealthStatus) => {
    switch (status) {
      case 'healthy':
        return 'success';
      case 'degraded':
        return 'warning';
      case 'error':
        return 'error';
      default:
        return 'default';
    }
  };

  const getStatusText = (status: HealthStatus) => {
    switch (status) {
      case 'healthy':
        return 'Healthy';
      case 'degraded':
        return 'Degraded';
      case 'error':
        return 'Error';
      default:
        return 'Unknown';
    }
  };

  const getResponseTimeColor = (responseTime: number) => {
    if (responseTime <= config.expectedResponseTime) return 'success';
    if (responseTime <= config.criticalResponseTime) return 'warning';
    return 'error';
  };

  const formatTimestamp = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString();
  };

  const formatUptime = (uptime?: number) => {
    if (uptime === undefined) return 'N/A';
    return `${uptime.toFixed(2)}%`;
  };

  return (
    <Card 
      sx={{ 
        height: '100%',
        border: health.status === 'error' ? 2 : 1,
        borderColor: health.status === 'error' ? 'error.main' : 'divider',
      }}
    >
      <CardContent>
        {/* Service Header */}
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            {getStatusIcon(health.status)}
            <Typography variant="h6" component="h3" noWrap>
              {config.displayName}
            </Typography>
          </Box>

          <Chip
            label={getStatusText(health.status)}
            color={getStatusColor(health.status) as any}
            size="small"
            variant="filled"
          />
        </Box>

        {/* Service Details */}
        <Box sx={{ mb: 2 }}>
          <Typography variant="body2" color="text.secondary" gutterBottom>
            {config.url}:{config.port}
          </Typography>

          {health.error && (
            <Typography variant="body2" color="error" sx={{ mt: 1 }}>
              Error: {health.error}
            </Typography>
          )}
        </Box>

        <Divider sx={{ my: 2 }} />

        {/* Metrics */}
        <Box sx={{ space: 2 }}>
          {/* Response Time */}
          <Box sx={{ mb: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
              <ResponseIcon fontSize="small" color="action" />
              <Typography variant="body2" color="text.secondary">
                Response Time
              </Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <Typography variant="body1" fontWeight="medium">
                {health.responseTime}ms
              </Typography>
              <Chip
                label={health.responseTime <= config.expectedResponseTime ? 'Fast' : 
                       health.responseTime <= config.criticalResponseTime ? 'Slow' : 'Critical'}
                color={getResponseTimeColor(health.responseTime) as any}
                size="small"
                variant="outlined"
              />
            </Box>
            <LinearProgress
              variant="determinate"
              value={Math.min((health.responseTime / config.criticalResponseTime) * 100, 100)}
              color={getResponseTimeColor(health.responseTime) as any}
              sx={{ mt: 1, height: 4, borderRadius: 2 }}
            />
          </Box>

          {/* Uptime */}
          {health.data?.uptime_percentage !== undefined && (
            <Box sx={{ mb: 2 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
                <TimeIcon fontSize="small" color="action" />
                <Typography variant="body2" color="text.secondary">
                  Uptime
                </Typography>
              </Box>
              <Typography variant="body1" fontWeight="medium">
                {formatUptime(health.data.uptime_percentage)}
              </Typography>
              <LinearProgress
                variant="determinate"
                value={health.data.uptime_percentage}
                color={health.data.uptime_percentage > 99 ? 'success' : 
                       health.data.uptime_percentage > 95 ? 'warning' : 'error'}
                sx={{ mt: 1, height: 4, borderRadius: 2 }}
              />
            </Box>
          )}

          {/* Version */}
          {health.data?.version && (
            <Box sx={{ mb: 2 }}>
              <Typography variant="body2" color="text.secondary">
                Version: {health.data.version}
              </Typography>
            </Box>
          )}

          {/* Last Check */}
          <Box>
            <Typography variant="caption" color="text.secondary">
              Last checked: {formatTimestamp(health.timestamp)}
            </Typography>
          </Box>
        </Box>

        {/* Additional Details */}
        {health.data?.details && (
          <>
            <Divider sx={{ my: 2 }} />
            <Box>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                System Resources
              </Typography>

              {health.data.details.cpu_usage !== undefined && (
                <Box sx={{ mb: 1 }}>
                  <Typography variant="caption">
                    CPU: {health.data.details.cpu_usage}%
                  </Typography>
                  <LinearProgress
                    variant="determinate"
                    value={health.data.details.cpu_usage}
                    color={health.data.details.cpu_usage > 80 ? 'error' : 
                           health.data.details.cpu_usage > 60 ? 'warning' : 'success'}
                    sx={{ height: 3, borderRadius: 1 }}
                  />
                </Box>
              )}

              {health.data.details.memory_usage !== undefined && (
                <Box sx={{ mb: 1 }}>
                  <Typography variant="caption">
                    Memory: {health.data.details.memory_usage}%
                  </Typography>
                  <LinearProgress
                    variant="determinate"
                    value={health.data.details.memory_usage}
                    color={health.data.details.memory_usage > 80 ? 'error' : 
                           health.data.details.memory_usage > 60 ? 'warning' : 'success'}
                    sx={{ height: 3, borderRadius: 1 }}
                  />
                </Box>
              )}

              {health.data.details.active_connections !== undefined && (
                <Typography variant="caption" display="block">
                  Active Connections: {health.data.details.active_connections}
                </Typography>
              )}
            </Box>
          </>
        )}
      </CardContent>

      <CardActions sx={{ justifyContent: 'space-between', px: 2, pb: 2 }}>
        <Button
          size="small"
          startIcon={<DiagnosticsIcon />}
          onClick={() => onViewDiagnostics?.()}
          variant="outlined"
        >
          Diagnostics
        </Button>

        <Tooltip title="Refresh Status">
          <IconButton size="small" onClick={() => onRefresh?.()}>
            <RefreshIcon />
          </IconButton>
        </Tooltip>
      </CardActions>
    </Card>
  );
};

