/**
 * Troubleshooting Panel Component
 * Displays troubleshooting suggestions and common solutions
 */

import React, { useState } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  Box,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Chip,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  IconButton,
  Tooltip,
  Alert,
  Link,
} from '@mui/material';
import {
  Close as CloseIcon,
  ExpandMore as ExpandMoreIcon,
  Error as CriticalIcon,
  Warning as HighIcon,
  Info as MediumIcon,
  CheckCircle as LowIcon,
  BugReport as IssueIcon,
  Build as SolutionIcon,
  Help as CauseIcon,
  Description as DocsIcon,
} from '@mui/icons-material';
import type { TroubleshootingPanelProps, TroubleshootingSuggestion } from '../types/health-types';

const TroubleshootingPanel: React.FC<TroubleshootingPanelProps> = ({
  suggestions,
  service,
  onClose,
}) => {
  const [expandedSuggestion, setExpandedSuggestion] = useState<string | false>(false);

  const getSeverityIcon = (severity: TroubleshootingSuggestion['severity']) => {
    switch (severity) {
      case 'critical':
        return <CriticalIcon color="error" />;
      case 'high':
        return <HighIcon color="warning" />;
      case 'medium':
        return <MediumIcon color="info" />;
      case 'low':
        return <LowIcon color="success" />;
      default:
        return <IssueIcon color="disabled" />;
    }
  };

  const getSeverityColor = (severity: TroubleshootingSuggestion['severity']) => {
    switch (severity) {
      case 'critical':
        return 'error';
      case 'high':
        return 'warning';
      case 'medium':
        return 'info';
      case 'low':
        return 'success';
      default:
        return 'default';
    }
  };

  const getSeverityText = (severity: TroubleshootingSuggestion['severity']) => {
    return severity.charAt(0).toUpperCase() + severity.slice(1);
  };

  const handleAccordionChange = (suggestionId: string) => (
    event: React.SyntheticEvent,
    isExpanded: boolean
  ) => {
    setExpandedSuggestion(isExpanded ? suggestionId : false);
  };

  const sortedSuggestions = [...suggestions].sort((a, b) => {
    const severityOrder = { critical: 0, high: 1, medium: 2, low: 3 };
    return severityOrder[a.severity] - severityOrder[b.severity];
  });

  return (
    <Dialog
      open={true}
      onClose={onClose}
      maxWidth="md"
      fullWidth
      PaperProps={{
        sx: { minHeight: '70vh', maxHeight: '90vh' }
      }}
    >
      <DialogTitle>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Typography variant="h6">
            Troubleshooting{service ? `: ${service}` : ''}
          </Typography>

          <Tooltip title="Close">
            <IconButton onClick={onClose} size="small">
              <CloseIcon />
            </IconButton>
          </Tooltip>
        </Box>
      </DialogTitle>

      <DialogContent>
        {suggestions.length === 0 ? (
          <Alert severity="success" sx={{ mt: 2 }}>
            <Typography variant="h6" gutterBottom>
              No Issues Detected
            </Typography>
            <Typography>
              All services are operating normally. No troubleshooting suggestions are available at this time.
            </Typography>
          </Alert>
        ) : (
          <>
            <Alert severity="info" sx={{ mb: 3 }}>
              <Typography variant="body2">
                Found {suggestions.length} issue{suggestions.length !== 1 ? 's' : ''} that may require attention. 
                Expand each item below for detailed troubleshooting steps.
              </Typography>
            </Alert>

            <Box>
              {sortedSuggestions.map((suggestion, index) => (
                <Accordion
                  key={`${suggestion.issue}-${index}`}
                  expanded={expandedSuggestion === `${suggestion.issue}-${index}`}
                  onChange={handleAccordionChange(`${suggestion.issue}-${index}`)}
                  sx={{ 
                    mb: 1,
                    border: suggestion.severity === 'critical' ? 2 : 1,
                    borderColor: suggestion.severity === 'critical' ? 'error.main' : 'divider',
                  }}
                >
                  <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    sx={{
                      backgroundColor: suggestion.severity === 'critical' ? 'error.light' : 
                                     suggestion.severity === 'high' ? 'warning.light' : 'transparent',
                      '&.Mui-expanded': {
                        backgroundColor: suggestion.severity === 'critical' ? 'error.light' : 
                                       suggestion.severity === 'high' ? 'warning.light' : 'action.hover',
                      },
                    }}
                  >
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, width: '100%' }}>
                      {getSeverityIcon(suggestion.severity)}

                      <Box sx={{ flexGrow: 1 }}>
                        <Typography variant="subtitle1" fontWeight="medium">
                          {suggestion.issue}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          {suggestion.description}
                        </Typography>
                      </Box>

                      <Chip
                        label={getSeverityText(suggestion.severity)}
                        color={getSeverityColor(suggestion.severity) as any}
                        size="small"
                        variant="filled"
                      />
                    </Box>
                  </AccordionSummary>

                  <AccordionDetails>
                    <Box sx={{ space: 3 }}>
                      {/* Possible Causes */}
                      <Box sx={{ mb: 3 }}>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
                          <CauseIcon color="primary" />
                          <Typography variant="h6">Possible Causes</Typography>
                        </Box>

                        <List dense>
                          {suggestion.possible_causes.map((cause, causeIndex) => (
                            <ListItem key={causeIndex}>
                              <ListItemIcon>
                                <Box
                                  sx={{
                                    width: 8,
                                    height: 8,
                                    borderRadius: '50%',
                                    backgroundColor: 'text.secondary',
                                  }}
                                />
                              </ListItemIcon>
                              <ListItemText primary={cause} />
                            </ListItem>
                          ))}
                        </List>
                      </Box>

                      {/* Suggested Actions */}
                      <Box sx={{ mb: 3 }}>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
                          <SolutionIcon color="primary" />
                          <Typography variant="h6">Suggested Actions</Typography>
                        </Box>

                        <List dense>
                          {suggestion.suggested_actions.map((action, actionIndex) => (
                            <ListItem key={actionIndex}>
                              <ListItemIcon>
                                <Typography variant="body2" color="primary" fontWeight="bold">
                                  {actionIndex + 1}.
                                </Typography>
                              </ListItemIcon>
                              <ListItemText primary={action} />
                            </ListItem>
                          ))}
                        </List>
                      </Box>

                      {/* Documentation Links */}
                      {suggestion.documentation_links && suggestion.documentation_links.length > 0 && (
                        <Box>
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
                            <DocsIcon color="primary" />
                            <Typography variant="h6">Documentation</Typography>
                          </Box>

                          <List dense>
                            {suggestion.documentation_links.map((link, linkIndex) => (
                              <ListItem key={linkIndex}>
                                <ListItemIcon>
                                  <DocsIcon fontSize="small" color="action" />
                                </ListItemIcon>
                                <ListItemText
                                  primary={
                                    <Link
                                      href={link}
                                      target="_blank"
                                      rel="noopener noreferrer"
                                      color="primary"
                                    >
                                      {link.split('/').pop() || link}
                                    </Link>
                                  }
                                />
                              </ListItem>
                            ))}
                          </List>
                        </Box>
                      )}
                    </Box>
                  </AccordionDetails>
                </Accordion>
              ))}
            </Box>

            {/* General Troubleshooting Tips */}
            <Box sx={{ mt: 4 }}>
              <Typography variant="h6" gutterBottom>
                General Troubleshooting Tips
              </Typography>

              <Alert severity="info" sx={{ mb: 2 }}>
                <Typography variant="body2" gutterBottom>
                  <strong>Before making changes:</strong>
                </Typography>
                <List dense>
                  <ListItem sx={{ py: 0 }}>
                    <ListItemText primary=" Check service logs for detailed error messages" />
                  </ListItem>
                  <ListItem sx={{ py: 0 }}>
                    <ListItemText primary=" Verify system resources (CPU, memory, disk space)" />
                  </ListItem>
                  <ListItem sx={{ py: 0 }}>
                    <ListItemText primary=" Test connectivity from different locations" />
                  </ListItem>
                  <ListItem sx={{ py: 0 }}>
                    <ListItemText primary=" Document any changes made for rollback purposes" />
                  </ListItem>
                </List>
              </Alert>

              <Alert severity="warning">
                <Typography variant="body2">
                  <strong>Need additional help?</strong> Contact your system administrator or 
                  check the comprehensive troubleshooting guide in the documentation.
                </Typography>
              </Alert>
            </Box>
          </>
        )}
      </DialogContent>

      <DialogActions>
        <Button onClick={onClose} variant="contained">
          Close
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default TroubleshootingPanel;

/**
 * Health Monitoring Hook
 * Provides comprehensive health monitoring functionality with auto-refresh and state management
 */

import { useState, useCallback, useEffect } from 'react';
import { 
  useCheckAllServicesHealthQuery,
  useGetSystemHealthOverviewQuery,
  healthApiClient,
} from '@infrastructure/api/health-api';
import type { 
  UseHealthMonitoringReturn,
  HealthCheckResult,
  SystemHealthOverview,
  HealthMonitoringConfig,
} from '../types/health-types';

const DEFAULT_CONFIG: HealthMonitoringConfig = {
  auto_refresh: true,
  refresh_interval: 30, // 30 seconds
  timeout: 10000, // 10 seconds
  retry_attempts: 3,
  alert_thresholds: {
    response_time_warning: 500,
    response_time_critical: 1000,
    uptime_warning: 99.0,
    uptime_critical: 95.0,
    error_rate_warning: 5.0,
    error_rate_critical: 10.0,
  },
};

export const useHealthMonitoring = (
  customConfig?: Partial<HealthMonitoringConfig>
): UseHealthMonitoringReturn => {
  const [config, setConfig] = useState<HealthMonitoringConfig>({
    ...DEFAULT_CONFIG,
    ...customConfig,
  });

  const [lastRefresh, setLastRefresh] = useState<string | null>(null);
  const [autoRefreshEnabled, setAutoRefreshEnabled] = useState(config.auto_refresh);

  // RTK Query hooks for data fetching
  const {
    data: servicesData,
    error: servicesError,
    isLoading: servicesLoading,
    refetch: refetchServices,
  } = useCheckAllServicesHealthQuery(undefined, {
    pollingInterval: autoRefreshEnabled ? config.refresh_interval * 1000 : 0,
    refetchOnMountOrArgChange: true,
    refetchOnFocus: true,
  });

  const {
    data: overviewData,
    error: overviewError,
    isLoading: overviewLoading,
    refetch: refetchOverview,
  } = useGetSystemHealthOverviewQuery(undefined, {
    pollingInterval: autoRefreshEnabled ? config.refresh_interval * 1000 : 0,
    refetchOnMountOrArgChange: true,
    refetchOnFocus: true,
  });

  // Combine loading states
  const loading = servicesLoading || overviewLoading;

  // Combine error states
  const error = servicesError || overviewError;
  const errorMessage = error ? 
    ('data' in error ? (error.data as any)?.message : 'message' in error ? error.message : 'Unknown error') : 
    null;

  // Manual refresh function
  const refresh = useCallback(async () => {
    try {
      await Promise.all([
        refetchServices(),
        refetchOverview(),
      ]);
      setLastRefresh(new Date().toISOString());
    } catch (error) {
      console.error('Failed to refresh health data:', error);
    }
  }, [refetchServices, refetchOverview]);

  // Toggle auto-refresh
  const toggleAutoRefresh = useCallback(() => {
    setAutoRefreshEnabled(prev => {
      const newValue = !prev;
      setConfig(prevConfig => ({
        ...prevConfig,
        auto_refresh: newValue,
      }));
      return newValue;
    });
  }, []);

  // Export report function
  const exportReport = useCallback(async (
    type: 'summary' | 'detailed' | 'historical'
  ): Promise<void> => {
    try {
      const report = await healthApiClient.exportHealthReport(type);

      // Create and download the report
      const blob = new Blob([JSON.stringify(report, null, 2)], {
        type: 'application/json',
      });

      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `health-report-${type}-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Failed to export health report:', error);
      throw error;
    }
  }, []);

  // Update last refresh when data changes
  useEffect(() => {
    if (servicesData || overviewData) {
      setLastRefresh(new Date().toISOString());
    }
  }, [servicesData, overviewData]);

  return {
    services: servicesData || {},
    overview: overviewData || null,
    loading,
    error: errorMessage,
    refresh,
    toggleAutoRefresh,
    exportReport,
  };
};

/**
 * Service Health Hook
 * Provides individual service health monitoring and diagnostics functionality
 */

import { useState, useCallback } from 'react';
import { 
  useCheckServiceHealthQuery,
  healthApiClient,
} from '@infrastructure/api/health-api';
import type { 
  UseServiceHealthReturn,
  HealthCheckResult,
  DiagnosticInfo,
} from '../types/health-types';

export const useServiceHealth = (serviceName: string): UseServiceHealthReturn => {
  const [diagnostics, setDiagnostics] = useState<DiagnosticInfo | null>(null);
  const [diagnosticsLoading, setDiagnosticsLoading] = useState(false);
  const [diagnosticsError, setDiagnosticsError] = useState<string | null>(null);

  // RTK Query hook for service health
  const {
    data: healthData,
    error: healthError,
    isLoading: healthLoading,
    refetch: refetchHealth,
  } = useCheckServiceHealthQuery(serviceName, {
    refetchOnMountOrArgChange: true,
    refetchOnFocus: true,
  });

  // Combine error states
  const error = healthError || diagnosticsError;
  const errorMessage = error ? 
    (typeof error === 'string' ? error : 
     'data' in error ? (error.data as any)?.message : 
     'message' in error ? error.message : 'Unknown error') : 
    null;

  // Manual refresh function
  const refresh = useCallback(async () => {
    try {
      await refetchHealth();
    } catch (error) {
      console.error(`Failed to refresh health data for ${serviceName}:`, error);
    }
  }, [refetchHealth, serviceName]);

  // Run diagnostics function
  const runDiagnostics = useCallback(async () => {
    setDiagnosticsLoading(true);
    setDiagnosticsError(null);

    try {
      const diagnosticsResult = await healthApiClient.runDiagnostics(serviceName);
      setDiagnostics(diagnosticsResult);
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Failed to run diagnostics';
      setDiagnosticsError(errorMessage);
      console.error(`Failed to run diagnostics for ${serviceName}:`, error);
    } finally {
      setDiagnosticsLoading(false);
    }
  }, [serviceName]);

  return {
    health: healthData || null,
    diagnostics,
    loading: healthLoading || diagnosticsLoading,
    error: errorMessage,
    refresh,
    runDiagnostics,
  };
};

/**
 * System Health Page Component
 * Provides real-time monitoring and diagnostics for all backend services
 */

import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardHeader,
  Button,
  IconButton,
  Switch,
  FormControlLabel,
  Alert,
  Chip,
  Tooltip,
  CircularProgress,
  Divider,
} from '@mui/material';
import {
  Refresh as RefreshIcon,
  Download as DownloadIcon,
  Settings as SettingsIcon,
  Timeline as TimelineIcon,
  BugReport as DiagnosticsIcon,
} from '@mui/icons-material';
import { useHealthMonitoring } from '../hooks/useHealthMonitoring';
import { HealthOverview } from '../components/HealthOverview';
import { ServiceStatusCard } from '../components/ServiceStatusCard';
import { DiagnosticsPanel } from '../components/DiagnosticsPanel';
import { TroubleshootingPanel } from '../components/TroubleshootingPanel';
import { SERVICE_CONFIGS } from '@infrastructure/api/health-api';
import { generateTroubleshootingSuggestions, exportHealthReport } from '../utils/troubleshooting';
import type { DiagnosticInfo, TroubleshootingSuggestion } from '../types/health-types';

const SystemHealthPage: React.FC = () => {
  const {
    services,
    overview,
    loading,
    error,
    refresh,
    toggleAutoRefresh,
    exportReport,
  } = useHealthMonitoring();

  const [selectedService, setSelectedService] = useState<string | null>(null);
  const [showDiagnostics, setShowDiagnostics] = useState(false);
  const [showTroubleshooting, setShowTroubleshooting] = useState(false);
  const [autoRefresh, setAutoRefresh] = useState(true);
  const [diagnosticsData, setDiagnosticsData] = useState<DiagnosticInfo | null>(null);
  const [troubleshootingSuggestions, setTroubleshootingSuggestions] = useState<TroubleshootingSuggestion[]>([]);

  // Auto-refresh effect
  useEffect(() => {
    if (autoRefresh) {
      const interval = setInterval(() => {
        refresh();
      }, 30000); // 30 seconds

      return () => clearInterval(interval);
    }
  }, [autoRefresh, refresh]);

  const handleRefresh = () => {
    refresh();
  };

  const handleToggleAutoRefresh = () => {
    setAutoRefresh(!autoRefresh);
    toggleAutoRefresh();
  };

  const handleExportReport = async (type: 'summary' | 'detailed' | 'historical') => {
    try {
      await exportHealthReport(type, services, overview, 'json');
    } catch (error) {
      console.error('Failed to export report:', error);
    }
  };

  const handleViewDiagnostics = async (serviceName: string) => {
    setSelectedService(serviceName);
    setShowDiagnostics(true);

    // In a real implementation, this would call the diagnostics API
    // For now, we'll simulate diagnostics data
    const mockDiagnostics: DiagnosticInfo = {
      service: serviceName,
      connectivity: {
        can_reach: services[serviceName]?.status !== 'error',
        dns_resolution: true,
        port_open: services[serviceName]?.status !== 'error',
      },
      performance: {
        response_time: services[serviceName]?.responseTime || 0,
        error_rate: services[serviceName]?.status === 'error' ? 100 : 0,
      },
      dependencies: {
        database: services[serviceName]?.status === 'healthy' ? 'healthy' : 'error',
        cache: 'healthy',
      },
      system_info: {
        version: services[serviceName]?.data?.version || '1.0.0',
        uptime: '99.9%',
        environment: 'production',
      },
    };

    setDiagnosticsData(mockDiagnostics);
  };

  const handleShowTroubleshooting = () => {
    setShowTroubleshooting(true);

    // Generate troubleshooting suggestions based on current issues
    const suggestions = generateTroubleshootingSuggestions(services);
    setTroubleshootingSuggestions(suggestions);
  };

  const getOverallStatusColor = () => {
    if (!overview) return 'default';
    switch (overview.overall_status) {
      case 'healthy': return 'success';
      case 'degraded': return 'warning';
      case 'error': return 'error';
      default: return 'default';
    }
  };

  const getOverallStatusText = () => {
    if (!overview) return 'Unknown';
    switch (overview.overall_status) {
      case 'healthy': return 'All Systems Operational';
      case 'degraded': return 'Some Issues Detected';
      case 'error': return 'Critical Issues';
      default: return 'Status Unknown';
    }
  };

  return (
    <Container maxWidth="xl" sx={{ py: 3 }}>
      {/* Page Header */}
      <Box sx={{ mb: 4 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h4" component="h1" fontWeight="bold">
            System Health
          </Typography>

          <Box sx={{ display: 'flex', gap: 1, alignItems: 'center' }}>
            <FormControlLabel
              control={
                <Switch
                  checked={autoRefresh}
                  onChange={handleToggleAutoRefresh}
                  size="small"
                />
              }
              label="Auto-refresh"
              sx={{ mr: 2 }}
            />

            <Tooltip title="Refresh Now">
              <IconButton onClick={handleRefresh} disabled={loading}>
                {loading ? <CircularProgress size={20} /> : <RefreshIcon />}
              </IconButton>
            </Tooltip>

            <Tooltip title="View Troubleshooting">
              <IconButton onClick={handleShowTroubleshooting}>
                <DiagnosticsIcon />
              </IconButton>
            </Tooltip>

            <Button
              variant="outlined"
              startIcon={<DownloadIcon />}
              onClick={() => handleExportReport('summary')}
              size="small"
            >
              Export Report
            </Button>
          </Box>
        </Box>

        {/* Overall Status */}
        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Chip
                label={getOverallStatusText()}
                color={getOverallStatusColor() as any}
                variant="filled"
                size="medium"
              />

              {overview && (
                <>
                  <Typography variant="body2" color="text.secondary">
                    {overview.healthy_services}/{overview.total_services} services healthy
                  </Typography>

                  <Divider orientation="vertical" flexItem />

                  <Typography variant="body2" color="text.secondary">
                    Avg Response: {overview.average_response_time}ms
                  </Typography>

                  <Divider orientation="vertical" flexItem />

                  <Typography variant="body2" color="text.secondary">
                    Uptime: {overview.overall_uptime}%
                  </Typography>

                  <Divider orientation="vertical" flexItem />

                  <Typography variant="body2" color="text.secondary">
                    Last Updated: {new Date(overview.last_updated).toLocaleTimeString()}
                  </Typography>
                </>
              )}
            </Box>
          </CardContent>
        </Card>
      </Box>

      {/* Error Alert */}
      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {/* Health Overview */}
      {overview && (
        <Box sx={{ mb: 4 }}>
          <HealthOverview overview={overview} onRefresh={handleRefresh} loading={loading} />
        </Box>
      )}

      {/* Service Status Cards */}
      <Typography variant="h5" component="h2" gutterBottom>
        Service Status
      </Typography>

      <Grid container spacing={3}>
        {Object.entries(SERVICE_CONFIGS).map(([serviceName, config]) => {
          const health = services[serviceName];

          return (
            <Grid item xs={12} md={6} lg={4} key={serviceName}>
              <ServiceStatusCard
                service={serviceName}
                health={health || {
                  service: serviceName,
                  status: 'unknown',
                  responseTime: 0,
                  timestamp: new Date().toISOString(),
                }}
                onRefresh={() => refresh()}
                onViewDiagnostics={() => handleViewDiagnostics(serviceName)}
              />
            </Grid>
          );
        })}
      </Grid>

      {/* Diagnostics Panel */}
      {showDiagnostics && selectedService && diagnosticsData && (
        <DiagnosticsPanel
          service={selectedService}
          diagnostics={diagnosticsData}
          onClose={() => {
            setShowDiagnostics(false);
            setSelectedService(null);
            setDiagnosticsData(null);
          }}
        />
      )}

      {/* Troubleshooting Panel */}
      {showTroubleshooting && (
        <TroubleshootingPanel
          suggestions={troubleshootingSuggestions}
          onClose={() => setShowTroubleshooting(false)}
        />
      )}
    </Container>
  );
};

export default SystemHealthPage;

