    changes: [
      {
        type: ChangeType.SUBSTITUTION,
        originalStart: 5,
        originalEnd: 8,
        correctedStart: 5,
        correctedEnd: 7,
        originalText: 'are',
        correctedText: 'is',
        confidence: 0.98
      },
      {
        type: ChangeType.SUBSTITUTION,
        originalStart: 35,
        originalEnd: 41,
        correctedStart: 35,
        correctedEnd: 47,
        originalText: 'errors',
        correctedText: 'corrections',
        confidence: 0.95
      }
    ]
  };

  const mockComparisonWithDifferentChangeTypes: TextComparison = {
